import React from 'react'
import '../../../styles/BandeauTopTact.css';


const CaseDonnee = ({
    name,
    value,
    primary,
    flexFactor
}) => {
     
    
    return ( 
        <div 
            className="case-panel"
            style={{
                flexDirection:primary?"column":"row",
                flex:flexFactor
            }} 
        > 
            <label 
                className="label-title"
                style={{
                    fontSize:!primary ?  15 : null
                }}
            >
                {name}
            </label>
           
               
                {
                    name=="Cycle actuel" ? 
                    <span className="case-value" >
                        {value+" "}<span style={{color:"red"}}>{" / "+6}</span>
                    </span>

                    :  

                    name=="Marqueurs dégâts" ? 
                    <span className="case-value" >
                        {value+" "}<span style={{color:"red"}}>{" / "+24}</span>
                    </span>
                    :
                    <span 
                    className="case-value"
                    > {value}</span>

                }
                
            
            
        </div>



    );
}
 
export default CaseDonnee;
