import { withWidth } from '@material-ui/core';
import zIndex from '@material-ui/core/styles/zIndex';
import React,{useState,useEffect} from 'react'

const MurPiece = ({
    num
}) => {
    const epaisseur=5
    
    const colors =[
        "purple",
        "blue",
        "white",
        "gray"
    ]

    const height = document.getElementById('test').clientHeight;
    console.log(height)


    return ( 
        <div 
            style={{
                
                position:"absolute",
                width:num%2 ? "100%" : epaisseur,
                height:num%2 ?  epaisseur : "100%",
                right:num<2 ? 0 :  null,
                left:num>1 ? 0 :  null,
                top:num<2 ? 0 :  null,
                bottom:num>1 ? 0 :  null,
                backgroundColor:colors[num],
            }}
        >
        </div>

    );
}
 
export default MurPiece;
