import React,{useState,useEffect} from 'react'
import CaseDonnee from './panels/CaseDonnee'
import '../styles/BandeauTop.css';

import ContentsAPI from "../API/ContentsAPI"

const BandeauTop = ({
    numeroPompier,
    cycle,
    tour,
    nbInterventions,
    nbExtFeu,
    nbExtFum,
    nbExtFeuFum,
    nbMarqueurs,
    nbDecedes,
    nbSauves

}) => {

    const [textes,setTextes]=useState(null)
    
    const renderFeu = ()=>{
    
        return (

            (tour%6)==numeroPompier ? 
            <button className="bouton-vert"></button>
            :
            <button className="bouton-rouge"></button>
        
        )
    }
    useEffect(()=>{
        console.log("use effect")
        const getTextes=async()=>{
            const textes = await ContentsAPI.getTexts("jeuBandeauTop")
            return textes
        }
        setTextes(getTextes() )
    },[])

    const getText=(name)=>{
        if(textes==null){
            return name
        }else{
            return textes[name]
        }
    }


    return (  
        <div className="bandeau-top">
            <CaseDonnee
                name={getText("cycle")}
                value={cycle}
                
            />
            <CaseDonnee
                name={"tour"}
                value={tour}
            />
            <CaseDonnee
                name={"nbInterventions"}
                value={nbInterventions}
            />
            <CaseDonnee
                name={"nbExtFeu"}
                value={nbExtFeu}
            />
            <CaseDonnee
                name={"nbExtFeuFum"}
                value={nbExtFeuFum}
            />
            <CaseDonnee
                name={"nbExtFum"}
                value={nbExtFum}
            />
            <CaseDonnee
                name={"nbMarqueurs"}
                value={nbMarqueurs}
            />
            <CaseDonnee
                name={"nbDecedes"}
                value={nbDecedes}
            />
            <CaseDonnee
                name={"nbSauves"}
                value={nbSauves}
            />
            <CaseDonnee
                name={"numPompier"}
                value={numeroPompier}
            />
            {renderFeu()}
        </div>
       

    );
}
 
export default BandeauTop;
