import './App.css';
import React,{useState,useEffect} from 'react'

import SearchField from './components/SearchField'
import ListCompanies from './components/ListCompanies'
import {createMuiTheme,ThemeProvider} from '@material-ui/core/styles';

import  './App.css'

function App() {

  const [companies,setCompanies]=useState([])
  const [input,setInput]=useState("")

  const handlerSearch=(companies,str)=>{
    setCompanies(companies)
    setInput(str)
  }

  const handlerClic=(company)=>{
    setInput(company.name)
  }
//dark, normal, light
  const blue={
    dark:"#006D73",
    main:"#46A9B4",
    light:"#B0FBFF"
  }
  const red={
    dark:"#A01314",
    main:"#EA4D4F",
    light:"#F8C4C4"
  }
  const purple={
    dark:"#573A66",
    main:"#9F79B3",
    light:"#DFD2E6"
  }



  const outerTheme=createMuiTheme({
    palette:{
      primary:{
        light:purple.light,
        main:purple.main,
        dark:purple.dark,
        contrastText:'black'
      },
      secondary:{
        light:red.light,
        main:red.main,
        dark:red.dark,
        contrastText:'black'
      }
    }
  })

  return (
    <div className="search">
      <ThemeProvider theme={outerTheme}>

      <SearchField 
        selectedCompany ={input}
        handlerSearch={handlerSearch}
      />
      <ListCompanies
        companies={companies}
        input={input}
        handlerClic={handlerClic}
      />

      </ThemeProvider>

    </div>

  );
}

export default App;
