import React, { useState, useEffect } from 'react'
import CaseDonnee from './panels/CaseDonnee'
import '../styles/BandeauTop.css';
import BookmarkRoundedIcon from '@material-ui/icons/BookmarkRounded';
import Typography from '@material-ui/core/Typography';
import Grid from '@material-ui/core/Grid';

import ContentsAPI from "../API/ContentsAPI"

const BandeauTop = ({
    numeroPompier,
    cycle,
    tour,
    nbInterventions,
    nbExtFeu,
    nbExtFum,
    nbExtFeuFum,
    nbMarqueurs,
    nbDecedes,
    nbSauves

}) => {

    const [textes, setTextes] = useState(null)

    useEffect(() => {
        console.log("use effect")
        const getTextes = async () => {
            const textes = ContentsAPI.getTexts("jeuBandeauTop").then(
                (res) => { setTextes(res) }
            )
        }
        if (textes == null) {
            getTextes()
        }
    }, [textes])

    const renderFeu = () => {

        return (

            <CaseDonnee
                name={getText("numPompier")}
                value={numeroPompier}
                primary={true}
                flexFactor={1}
            />

        )
    }


    const getText = (name) => {
        if (textes == null) {
            return name
        } else {
            return textes["label" + name.charAt(0).toUpperCase() + name.slice(1)].contenu
        }
    }

    const renderStocks = () => {
        return (
            <div
                className="case-panel donnees-container"
                style={{
                    flex: 3
                }}
            >
                <label
                    className="label-title"
                >
                    {getText("stockEquipements")}
                </label>
                <div className="sous-donnees-container">
                    <CaseDonnee
                        name={getText("nbExtFeu")}
                        value={nbExtFeu}
                        primary={false}
                        flexFactor={1}
                    />
                    <CaseDonnee
                        name={getText("nbExtFum")}
                        value={nbExtFum}
                        primary={false}
                        flexFactor={1}
                    />
                </div>
                <div className="sous-donnees-container">
                    <CaseDonnee
                        name={getText("nbExtFeuFum")}
                        value={nbExtFeuFum}
                        primary={false}
                        flexFactor={2}
                    />
                    <CaseDonnee
                        name={getText("nbMarqueurs")}
                        value={nbMarqueurs}
                        primary={false}
                        flexFactor={2}
                    />
                </div>
            </div>
        )
    }

    const renderResults = () => {
        return (
            <div
                className="case-panel donnees-container"
                style={{
                    flex: 2
                }}
            >
                <label
                    className="label-title"
                >
                    {getText("resultats") + " " + numeroPompier}
                </label>
                <CaseDonnee
                    name={getText("nbDecedes")}
                    value={nbDecedes}
                    primary={false}
                    flexFactor={0}
                />
                <CaseDonnee
                    name={getText("nbSauves")}
                    value={nbSauves}
                    primary={false}
                    flexFactor={0}
                />
            </div>
        )
    }

    const renderNumPompier = () => {
        return (
            <div
                className="case-panel"
            >
                <label
                    className="label-title"
                >
                    {getText("numPompier")}
                </label>

                <div className="donnees-container">
                    <label
                        className="label-title label-num-pompier"
                    >
                        {numeroPompier}
                    </label>

                    <BookmarkRoundedIcon
                        className="icone-num-pompier"
                        fontSize="large"
                        stroke={"white"}
                        stroke-width={1}
                    />
                </div>




            </div>
        )
    }

    const renderCircleTours = () => {
        var type = 1, //circle type - 1 whole, 0.5 half, 0.25 quarter
            radius = 20, //distance from center
            start = -90, //shift start from 0
            // $elements = $('li:not(:first-child)'),
            numberOfElements = 6,
            slice = 360 * type / numberOfElements;

        const renderCircleItems = () => {
            var items = []
            for (let i = 0; i < numberOfElements; i++) {
                const rotate = slice * i + start
                const rotateReverse = rotate * -1
                items.push(
                    <li 
                    style={{ 
                        'transform': 'rotate(' + rotate + 'deg) translate(' + radius + 'px) rotate(' + rotateReverse + 'deg) translate(' + -radius/10/2 + 'px)',
                        "backgroundColor":i+1==tour ? "#BF0000" : null
                    }}
                    
                    >
                        {i + 1}
                    </li>

                )
            }
            return items
        }

        return (
            <div
                className="case-panel"
                style={{
                    // flexDirection:primary?"column":"row",
                    // flex:flexFactor
                }}
            >
                <label
                    className="label-title"
                >
                    {getText("tourParTour")}
                </label>
                <ul >
                    {renderCircleItems()}
                </ul>
            </div>
        )
    }

    return (
        <div className="bandeau-top">
            <div className="cases-container">
                <CaseDonnee
                    name={getText("cycle")}
                    value={cycle}
                    primary={true}
                    flexFactor={1}

                />
                <CaseDonnee
                    name={getText("tour")}
                    value={tour}
                    primary={true}
                    flexFactor={1}
                />
                <CaseDonnee
                    name={getText("nbInterventions")}
                    value={nbInterventions}
                    primary={true}
                    flexFactor={1}
                />
                {renderStocks()}
                {renderResults()}
                {renderCircleTours()}
                {renderNumPompier()}
                {renderFeu()}

            </div>

        </div>


    );
}

export default BandeauTop;
