import React,{useRef,useState,useEffect,useLayoutEffect} from 'react'
import MurPiece from "./MurPiece"

const CasePiece = ({
    murs,
    type,
    coordX,
    coordY,
    nbColonnesPiece,
    nbLignesPiece,
    nbColonnesGrille,
    nbLignesGrille,
    anchor,
    propositionMursDetruire,
    propositionsCase,
    handleClicMur,
    mursH,
    mursV
}) => {
    const [height,setHeight]=useState(0)
    const [width,setWidth]=useState(0)
    
    const colors=[
        "#EEEEEE", // 0 vide
        "white", // 1 pompier
        "green", // 2 habitant
        "red", // 3 pompier + habitant
        "#EEEEEE", // 4 fumée
        "#EEEEEE", // 5 flamme
        
    ]


    const element =useRef()
    
    useLayoutEffect(() => {
        function updateSize() {
            setHeight(element.current.clientHeight)
            setWidth(element.current.clientWidth)
        }
        window.addEventListener('resize', updateSize);
        updateSize();
        return () => window.removeEventListener('resize', updateSize);
    }, []);

    const renderMurs=()=>{
        var mursComponents=[]
        for(let i=0;i<4;i++){
            
            mursComponents.push(
                <MurPiece 
                    key={i}
                    num={i} 
                    height={height}
                    width={width}
                    type={murs[i]}
                    coordX={coordX}
                    coordY={coordY}
                    anchor={anchor}
                    nbColonnesPiece={ nbColonnesPiece}
                    nbLignesPiece={nbLignesPiece}
                    nbColonnesGrille={nbColonnesGrille}
                    nbLignesGrille={nbLignesGrille}
                    propositionMursDetruire={propositionMursDetruire}
                    handleClicMur={handleClicMur}
                    mursH={mursH}
                    mursV={mursV}
                />
            )
        }
        return (
            <>
                {mursComponents}
            </>
        )
    }



    const renderCase=()=>{
        let opacity=0
        let source=""
        if (type==1){
            opacity=1
            source="assets/Pompier.png"
        }
        // if (type==2) {
        //     opacity=1
        //     source="assets/Pompier.png"
        // }
        if (type==4) {
            opacity=1
            source="assets/Fumée"+(coordX+coordY)%5+".png"
        }
        if (type==5) {
            opacity=1
            source="assets/Flamme.png"
        }
        
        return (<img style={{maxHeight:height*0.9,maxWidth:width*0.8,transition:"1s",opacity:opacity}} src={source} />)
    }

    const checkInCoords=()=>{
        var checked=false
        propositionsCase.forEach((coords) => {
            if(coords[0]==coordY && coords[1]==coordX) checked =true
        });
        return checked

    }
  
    const [clignotant, setClignotant] = useState(false)


    useEffect(() => {
        setClignotant(checkInCoords())
    })


    return ( 

        <div 
        
            ref={ element }
            className={clignotant ? "clignotant case-piece" : "case-piece"}
            style={{
                backgroundColor: type >=0 ? colors[type] : "#D1D1D1", // -1 caché"
                transition:"1s"
            }}
            
        >
           {renderMurs()}
           {renderCase()}

        </div>

    );
}
 
export default CasePiece;
