import React from 'react';
import { makeStyles } from '@material-ui/core/styles';
import CasePiece from "./CasePiece"



const Piece = ({
    carte,
    idPiece,
    position,
    nbLignes,
    nbColonnes,
    maxColonnesPiece,
    maxLignesPiece,
    mursV,
    mursH,
    anchor

}) => {
    console.log("Piece nb lignes : "+nbLignes+", colonnes : "+nbColonnes+" => anchor i : "+anchor[0]+", j : "+anchor[1])
    const renderRow=(i)=>{
        var row=[]
        for(let j=0;j<nbColonnes+2;j++){
            const anchori = i+anchor[0]-1
            const anchorj = j+anchor[1]-1
            
            if (anchori<nbLignes+anchor[0] &&  anchori>=anchor[0] && anchorj<nbColonnes+anchor[1] &&  anchorj>=anchor[1] ){
                // console.log("créé : ",anchori , anchorj)
                row.push( 
                    <CasePiece 
                        key={i+","+j}
                        coordX={j}
                        coordY={i}
                        maxColonnesPiece={maxColonnesPiece}
                        maxLignesPiece={maxLignesPiece}
                        murs={[mursH[anchori][anchorj],mursV[anchori][anchorj+1],mursH[anchori+1][anchorj],mursV[anchori][anchorj]]}
                        type={ j>nbColonnes || i>nbLignes ? 0 : carte[anchori][anchorj] }
                        
                    />
                )
            }else{
               
                let newI = anchori
                let newJ = anchorj
                var murs =[0,0,0,0]
                
                if(anchori<anchor[0]){ // haut
                    
                    murs[1]=mursV[anchori+1][newJ+1]>0 ? mursV[anchori+1][newJ+1] : 0
                    murs[2]=mursH[anchori+1][newJ]>0 ? mursH[anchori+1][newJ] : 0
                    // murs[3]=mursV[anchori+2][newJ]>0 ? mursV[anchori+2][newJ] : 0
                }
                if(anchori>=nbLignes+anchor[0]){ // bas
                    console.log("new ",newI,newJ,mursH[newI][newJ])
                    // murs[0]=mursH[newI][newJ]>0 ? mursV[newI][newJ] : 0
                    // murs[1]=mursV[newI+1][newJ+1]>0 ? mursH[newI+1][newJ+1] : 0
                    // murs[3]=mursV[newI+1][newJ]>0 ? mursV[newI+1][newJ] : 0
                }
                if(anchorj<anchor[1]){ // gauche

                }
                if(anchorj>=nbColonnes+anchor[1]){ // droite

                }



                console.log("annulé : ",anchori , anchorj)
                // newI=0
                // newJ=0
                row.push( 
                    <CasePiece 
                        key={i+","+j}
                        coordX={newJ}
                        coordY={newI}
                        maxColonnesPiece={maxColonnesPiece}
                        maxLignesPiece={maxLignesPiece}
                        // murs={[
                        //     mursH[newI][newJ]>0 ? mursH[newI][newJ] : 0,
                        //     mursV[newI][newJ+1]>0 ? mursH[newI][newJ+1] : 0,
                        //     mursH[newI+1][newJ]>0 ? mursH[newI+1][newJ] : 0,
                        //     mursV[newI][newJ]>0 ? mursH[newI][newJ] : 0
                        // ]}
                        murs={murs}

                        type={5}
                        
                    /> 
                )
                

            }
            
        }
        return row
    }


    const renderGrille=()=>{
        var grille=[]
        for (let i=0;i<nbLignes+2;i++){
            grille.push(
                <div key={"row"+i} className="row-piece" >
                    {renderRow(i)}
                </div>
            )  
            
        }
        return grille
    }



    return (  

    <div
        className="piece"  
        style={{
            position:"absolute",
            width:nbColonnes/maxColonnesPiece*80+"%",
            height:nbLignes/maxLignesPiece*100+"%",
            
        }} 
       
    >
       {renderGrille()}
    </div>
        
        
    );
}
 
export default Piece;
