import '../App.css';
import { useEffect, useState } from 'react';
import {TextField} from "@material-ui/core"
import UsersAPI from "../API/UsersAPI"
import { useHistory } from 'react-router-dom';

const  Login=({
  handleLogin,
  userConnected

})=> {
  const history = useHistory()

  const [user,setUser]=useState(userConnected)

  const handleConnect=async(e)=>{
    e.preventDefault()
    const response = await UsersAPI.login(dataConnection)
    if(response.id!=null) {
      setUser(response)
    }else{
      handleLogin(null)
    }
  } 

  useEffect(()=>{
    if(user!=null){
      handleLogin(user)
      // console.log("pushed")
      if(user.role.includes("POMP")){
        history.push("/tact");
      }else if(user.role.includes("POMP")){
        history.push("/inter");
      }
      
    }
  },[user])

  const [dataConnection,setDataConnection]=useState({
    identifiant:"",
    mdp:""
  })

  

  const onChange=(e)=>{
    var data = {
      identifiant:dataConnection.identifiant,
      mdp:dataConnection.mdp
    }
    data[e.target.name]=e.target.value
    setDataConnection(data)
  }



  return (
    <form onSubmit={handleConnect} method="post">
       <TextField
          required
          label="Identifiant"
          variant="outlined"
          name={"identifiant"} 
          value={dataConnection.identifiant}
          onChange={onChange}
        />
        <TextField
          required
          label="Mot de passe"
          variant="outlined"
          name="mdp" 
          value={dataConnection.mdp}
          onChange={onChange}
          type="password"
        />
      <input type="submit" name={"submit"} value={"Envoyer"} />

    </form>
  );
}

export default Login;
