import React, { useEffect, useState,useRef } from 'react';
import Grid from "@material-ui/core/Grid"
import Paper from "@material-ui/core/Paper"
import Table from '@material-ui/core/Table';
import TableBody from '@material-ui/core/TableBody';
import TableCell from '@material-ui/core/TableCell';
import TableContainer from '@material-ui/core/TableContainer';
import TableHead from '@material-ui/core/TableHead';
import TableRow from '@material-ui/core/TableRow';
import InputLabel from '@material-ui/core/InputLabel';
import MenuItem from '@material-ui/core/MenuItem';
import FormHelperText from '@material-ui/core/FormHelperText';
import FormControl from '@material-ui/core/FormControl';
import Select from '@material-ui/core/Select';
import TextField from '@material-ui/core/TextField';

import '../../styles/Interfaces.css';


const InterInterventions = ({
    budget,
    cycles
}) => {
    const lastRow = useRef(null)
    

    const nbPompiers = 6
    
    const [numCycle,setNumCycle]=useState(1)
    const [lastCycle, setLastCycle] = useState(
        {
            numCycle: 1,
            interPomp1: 100,
            interPomp2: 200,
            interPomp3: 300,
            interPomp4: 400,
            interPomp5: 500,
            interPomp6: 600,
        })



    const renderHeaders = () => {
        var components = []
        components.push(
            <TableCell key={0} align="center">Cycle</TableCell>
        )
        for (let i = 1; i <= nbPompiers; i++) {
            components.push(
                <TableCell key={i} align="center">Pompier {i}</TableCell>
            )
        }
        components.push(
            <TableCell key={1000} align="center">Total</TableCell>
        )
        return (
            <TableRow>
                {components}
            </TableRow>
        )
    }


    const styles = {
        root: {
          background: "black"
        },
        input: {
          color: "white"
        }
      };
      
    const renderBudgetsCycles = () => {
        const cyclesSorted = cycles.sort((a, b) => a.numCycle - b.numCycle)
        var components = []
        cyclesSorted.forEach((cycle) => {
            var comp = []
            var sum = 0
            comp.push(
                <TableCell scope="row" align="center" component="th" ref={lastRow}>
                    Cycle n°{cycle.numCycle}
                </TableCell>
            )

            Object.keys(cycle).forEach((key, index) => {
                if (key != "numCycle") {
                    comp.push(
                        <TableCell key={key} align="center">{renderSelect(cycle["numCycle"],numCycle != cycle["numCycle"], index, cycle[key])}</TableCell>
                    )
                    sum += cycle[key]
                }
            })
            comp.push(
                <TableCell scope="row" align="center"  >
                    <TextField 
                    id="outlined-basic" 
                    variant="outlined" 
                    disabled 
                    value= {sum + " €"} 
                    inputProps={{
                        style: { 
                            color: numCycle == cycle["numCycle"] ? sum>budget.sequence.total -budget.sequence.depense ? "red" : "green" : null,
                            fontWeight:numCycle == cycle["numCycle"] ? "bold" : null,
                            fontStyle: numCycle != cycle["numCycle"] ? "italic" : null,
                            textAlign:"center",
                            backgroundColor:"#f1f1f1",
                            borderRadius:5,
                            minWidth:70

                        },
                      }}
                    />
                       

                </TableCell>
            )
            components.push(
                <TableRow hover key={cycle.numCycle}>
                    {comp}
                </TableRow>
            )
            
        })
        return (
            components
        )
    }

    const handleChangeSelect = (numPompier, event) => {
        var c = [...cycles].sort((a, b) => a.numCycle - b.numCycle)
        c[c.length - 1]["interPomp" + numPompier] = event.target.value
        setCycles(c)
        //console.log(numPompier,event.target.value)
    }

    useEffect(() => {
        setNumCycle(cycles.length)
        lastRow.current.scrollIntoView()    
    }, [cycles])


    const renderPropositionsBudget = (nCycle,numPompier) => {
        const cycle = cycles.sort((a, b) => a.numCycle - b.numCycle)[nCycle-1]
        let sum=0
        Object.keys(cycle).forEach((key, index) => {
            if (key != "numCycle" && (numPompier!==index && cycle.numCycle === numCycle)) {
                sum += cycle[key]
            }
        })
        var components = []
        for (let i = 0; i <= Math.floor((budget.sequence.total - budget.sequence.depense-sum) / 100); i++) {
            components.push(
                <MenuItem value={i * 100} align="right" >{i * 100 + "€"}</MenuItem>
            )
        }
        return components
    }

    const renderSelect = (nCycle,disabled, numPompier, value) => {
        var components = []
        components.push(
            <FormControl disabled={disabled} variant="outlined">
                <Select
                    value={value}
                    onChange={(value) => handleChangeSelect(numPompier, value)}
                    // displayEmpty
                    color="secondary"
                >
                    <MenuItem value="" disabled align="center">
                        Budget {numPompier}
                    </MenuItem>

                    {renderPropositionsBudget(nCycle,numPompier)}

                </Select >
            </FormControl>
        )
        return (
            components
        )

    }


    return (
        <div className="container">
            <TableContainer component={Paper} className="tableau">
                <Table stickyHeader aria-label="simple table">
                    <TableHead>
                        {renderHeaders()}
                    </TableHead>
                    <TableBody>
                        {renderBudgetsCycles()}
                    </TableBody>
                </Table>
            </TableContainer>
        </div>
    )
}

export default InterInterventions;
