import { withWidth } from '@material-ui/core';
import zIndex from '@material-ui/core/styles/zIndex';
import React, { useState, useEffect } from 'react'

const MurPiece = ({
    num,
    height,
    width,
    type,
    coordX,
    coordY,
    maxColonnesPiece,
    maxLignesPiece
}) => {
    const epaisseurInit = 1
    const epaisseur = type > 0 ? type > 2 ? epaisseurInit * 8 : epaisseurInit * 4 : 1
    const [mounted,setMounted]=useState(false)
    const [cotePoigneeStart,setCotePoigneeStart]=useState(true)
    const [transform,setTranform]=useState([0,0])
    // console.log(type)

    const colors = [
        "", // 0 simple
        "black", // 1 large
        "green", // 2 marqueur dégat
        "#490000",//3 porte closed
        "#490000",//4 porte open
    ]

    const getTransform = () => {


        if (num == 0 || num == 2) { // horizontal
            var facteurX = 1 // droite par défaut
            var facteurY = -1
            if (coordY <2) {
                facteurX = -1
            }
            if (coordY <= maxLignesPiece / 2 && coordX <= maxColonnesPiece / 2 || coordY<2) {
                facteurY = 1
            }
            if (type == 3) {
                facteurX = 0
            }
            const translateX = facteurX * width * 0.9
            const translateY = facteurY * epaisseur / 1.5
            return [translateX,translateY]
            // return 'translate(' + translateX + "px," + translateY + "px)"
        }



        if (num == 1 || num == 3) { // vertical
            var facteurX = 1
            var facteurY = -1
            
            if (coordX > maxColonnesPiece / 2  || coordX ==maxColonnesPiece) {
                facteurX = -1
            }


            if (type == 3) {
                facteurY = 0
            }
            const translateX = facteurX * epaisseur / 1.5
            const translateY = facteurY * height * 0.9
            return [translateX,translateY]

            // return 'translate(' + translateX + "px," + translateY + "px)"
        }
    }

    const getOpacity = () => {
        // haut
        if(num==2){
            if (coordY <= maxLignesPiece / 2 && coordX <= maxColonnesPiece / 2 || coordY<2) return 0
            
        }
        if(num==0){
            if (!(coordY <= maxLignesPiece / 2 && coordX <= maxColonnesPiece / 2 || coordY<2)) return 0
            
        }

        if(num==1){
            if(!(coordX > maxColonnesPiece / 2  || coordX ==maxColonnesPiece)) return 0
        }
        if(num==3){
            if((coordX > maxColonnesPiece / 2  || coordX ==maxColonnesPiece)) return 0
        }
        // if (coordY <= maxLignesPiece / 2 && coordX <= maxColonnesPiece / 2 && num == 2) return 0
        

        // if (num == 1 && !(coordX > maxColonnesPiece / 2 + 2 || coordX == 5)) return 0
        // if (num == 3 && coordX == 6) return 0

        return 1
    }



    useEffect(()=>{
        setMounted(true)
        setTranform(getTransform())
    })



   


    const test = () => {
        console.log("coords X " + coordX + " " + maxColonnesPiece)
        console.log("coords Y " + coordY + " " + maxLignesPiece)
    }

    const renderPoignee = () => {
        let component = []

        switch (num) {
            case 0:
                component.push(
                    <div // trait
                        key={0}
                        style={{
                            position: "absolute",
                            top: epaisseur,
                            height: epaisseur / 3,
                            backgroundColor: "#9F9F9F",
                            width: epaisseur / 2,
                            right: width * 0.2,
                            border: "1px solid black",
                            

                        }} />
                )
                component.push(
                    <div // rond
                        key={1}
                        style={{
                            position: "absolute",
                            top: epaisseur * 1.3,
                            height: epaisseur,
                            background: "linear-gradient(20deg, rgba(100,100,100,1) 0%, rgba(194,194,194,1) 68%, rgba(236,236,236,1) 100%)",
                            width: epaisseur,
                            right: width * 0.2 - epaisseur / 2 + epaisseur / 4,
                            borderRadius: "100%",
                            border: "1px solid black",
                            boxShadow:"-1px 1px 1px 1px #9F9F9F"
                        }} />
                )
                break;


            case 2:
                component.push(
                    <div
                        key={2}

                        style={{
                            position: "absolute",
                            top: -epaisseur * 0.3 - 2,
                            height: epaisseur / 3,
                            backgroundColor: "#9F9F9F",
                            width: epaisseur / 2,
                            right: width * 0.2,
                            border: "1px solid black"
                        }} />
                )
                component.push(
                    <div
                        key={3}

                        style={{
                            position: "absolute",
                            top: -epaisseur * 1.3 - 2,
                            height: epaisseur,
                            background: "linear-gradient(20deg, rgba(100,100,100,1) 0%, rgba(194,194,194,1) 68%, rgba(236,236,236,1) 100%)",
                            width: epaisseur,
                            right: width * 0.2 - epaisseur / 2 + epaisseur / 4,
                            borderRadius: "100%",
                            border: "1px solid black",
                            boxShadow:"-1px 1px 1px 1px #9F9F9F"

                        }} />
                )
                break;
            case 1:
                component.push(
                    <div
                        key={4}
                        style={{
                            position: "absolute",
                            bottom: height * 0.2,
                            height: epaisseur / 3,
                            backgroundColor: "#9F9F9F",
                            width: epaisseur / 3,
                            right: epaisseur,
                            border: "1px solid black"
                        }} />
                )
                component.push(
                    <div
                        key={5}
                        style={{
                            position: "absolute",
                            bottom: height * 0.2 - epaisseur / 6 - 1,
                            height: epaisseur,
                            background: "linear-gradient(20deg, rgba(100,100,100,1) 0%, rgba(194,194,194,1) 68%, rgba(236,236,236,1) 100%)",
                            width: epaisseur,
                            right: epaisseur * 1.3,
                            borderRadius: "100%",
                            border: "1px solid black",
                            boxShadow:"-1px 1px 1px 1px #9F9F9F"

                        }} />

                )
                break;
            case 3:
                component.push(
                    <div
                        key={6}
                        style={{
                            position: "absolute",
                            bottom: height * 0.2,
                            height: epaisseur / 3,
                            backgroundColor: "#9F9F9F",
                            width: epaisseur / 3,
                            left: epaisseur,
                            border: "1px solid black"
                        }} />
                )
                component.push(
                    <div
                        key={7}
                        style={{
                            position: "absolute",
                            bottom: height * 0.2 - epaisseur / 6 - 1,
                            height: epaisseur,
                            background: "linear-gradient(20deg, rgba(100,100,100,1) 0%, rgba(194,194,194,1) 68%, rgba(236,236,236,1) 100%)",
                            width: epaisseur,
                            left: epaisseur * 1.3,
                            borderRadius: "100%",
                            border: "1px solid black",
                            boxShadow:"-1px 1px 1px 1px #9F9F9F"

                        }} />
                )
                break;
        }


        return (component)
    }

    return (
        <div
            key={8}
            onClick={() => test()}
            style={{
                position: "absolute",
                width: num % 2 == 0 ? width + 2 : epaisseur,
                height: num % 2 == 1 ? height + 1 : epaisseur,
                right: num == 1 ? -1 : null,
                left: num % 2 == 0 || num == 3 ? -1 : null,
                top: num == 0 || num % 2 == 1 ? -1 : null,
                bottom: num == 2 ? 0 : null,
                backgroundColor: (type > 0) ? colors[type] : 'transparent',
                zIndex: type == 3 || type == 4 ? 1 : 2,
                transform: type == 3 || type == 4 ? 'translate(' + transform[0] + "px," + transform[1]  + "px)" : '',
                opacity: type == 3 || type == 4 ? getOpacity() : 1,
                transition: type > 1 && mounted ? "1s" : "",
                borderRadius: type > 2 ? 3 : 0,
                border: type > 2 ? "1px solid black" : "",
                boxShadow: type > 2 ?  "-1px 1px 1px 1px #9F9F9F" :""
            }}
        >
            {
                type == 3 || type == 4 ?

                    renderPoignee()

                    :
                    null
            }
        </div>

    );
}

export default MurPiece;
