import { withWidth } from '@material-ui/core';
import zIndex from '@material-ui/core/styles/zIndex';
import React,{useState,useEffect} from 'react'

const MurGrille = ({
    type,
    position,
    vertical
}) => {

    const largeurMur=10
    
    const colorsMur=[
        "gray", // VIDE
        "black" //Mur
    ]
    return ( 
       <div 
            className="mur" 
            style={{
                position:"absolute",
                left:position==3 ? 0 : null,
                backgroundColor:colorsMur[type],
                width:vertical ? (type==1 ? largeurMur : 1 ) : "16.68%",
                height:vertical ?  "5%" : (type==1 ? largeurMur : 1 ) ,
                marginTop:position==2 ? "auto" : null,
                marginBottom:position==2 ? 0 : null,
                zIndex:type>0 ? 1 : 0
            }}>
       </div>

    );
}
 
export default MurGrille;
