          numClic = 3  // case en question
          // console.log("left",numClic)
        }
        if (coordY == position[0] && (coordX > position[1] || (coordX == position[1] && num == 1))) { // right
          numClic = 1  // case en question
          // console.log("right",numClic)
        }

        return numClic
      }

