import React from 'react';
import { makeStyles } from '@material-ui/core/styles';
import CaseGrille from "../CaseGrille"
import MurGrille from "../MurGrille"

import Grid from '@material-ui/core/Grid';
import '../Grille.css';




const Piece1 = ({carte,position}) => {

    const nbLignes=3
    const nbColonnes=3


    const renderGrille=()=>{
        var grille=[]
        for (let i=0;i<nbLignes;i++){
             for(let j=0;j<nbColonnes;j++){
                grille.push( 
                    <Grid 
                        key={i+","+j} 
                        item
                        xs={3} 
                    >
                        <CaseGrille 
                            murs={[0,1,2,3]}
                            type={carte[i+position[1]][j+position[0]]}
                        />
                         {/* <MurGrille 
                            type={0}
                            vertical={true}
                        /> */}
                    </Grid> 
                )
             }
            
        }
        return grille
    }



    return (  

    <Grid container className="grille">
       {renderGrille()}
    </Grid>
        
        
    );
}
 
export default Piece1;
