import io from 'socket.io-client'

const urlBack = "http://localhost:4000";
const socket = io.connect(urlBack)

export default {
    companies: async(input,els)=>{
        
        const requestOptions = {
            method: 'POST',
            headers: { 'Content-Type': 'application/json' },
            body: JSON.stringify({input:input,els:els})
        };

       
        const response = await  fetch(urlBack+"/companies", requestOptions)
        console.log(fetch(urlBack+"/companies", requestOptions))
        const data = await response.json();
          
        return data
    },
    nouveauTour:async(numPompier)=>{
        socket.emit('tour',parseInt(numPompier))
        console.log("nouveau tout api")
    }
};
