import React,{useState,useEffect} from 'react'

const CaseGrille = ({murs}) => {
    
    const colorMur="#1a1a1a"
    const colorVide="#d9d9d9"
    
    return ( 
       <div 
       className="case-grille" 
       style={{
            borderTopColor:murs[0] ? colorMur : colorVide,
            borderRightColor:murs[1] ? colorMur : colorVide,
            borderBottomColor:murs[2] ? colorMur : colorVide,
            borderLeftColor:murs[3] ? colorMur : colorVide,
            borderTopWidth:murs[0] ? 5 : 1,
            borderRightWidth:murs[1] ? 5 : 1,
            borderBottomWidth:murs[2] ? 5 : 1,
            borderLeftWidth:murs[3] ? 5 : 1,
       }}>

       </div>

    );
}
 
export default CaseGrille;
