import axios from "axios";
const headers = {
  "Content-Type": "application/json"
};
const urlBack = "http://localhost:4000";

export default {
    companies: (str)=>{
        const response= axios.post(
            urlBack+"/companies",
            {
                str
            },
            { 
               headers: headers
            }
        )
        .then(async (response) =>{
            return await response.data;
          })

        console.log(response)
          
        //return response
    }
};
