import React,{useState,useEffect} from 'react'
import './BandeauBottom.css';
import BoutonAction from "./BoutonAction"

const BandeauBottom = ({
    nbInterFeu,
    nbInterFumee,
    nbInterFeuFum,
    nbInterMarqueurs,
    nbInterOuvrirPorte,
    nbInterFermerPorte,
    nbInterHabitant
}) => {
    

    return (  
        <div className="bandeau-bottom">
            <BoutonAction 
                action="Eteindre un feu"
                nb={nbInterFeu}
            />
            <BoutonAction 
                action="Atténuer le feu en fumée"
                nb={nbInterFeuFum}
            />
            <BoutonAction 
                action="Stopper la fumée"
                nb={nbInterFumee}
            />
            <BoutonAction 
                action="Détruire porte/mur"
                nb={nbInterMarqueurs}
            />
            <BoutonAction 
                action="Ouvrir la porte"
                nb={nbInterOuvrirPorte}
            />
            <BoutonAction 
                action="Fermer la porte"
                nb={nbInterFermerPorte}
            />
            <BoutonAction 
                action="Transporter un habitant"
                nb={nbInterHabitant}
            />
        </div>
       

    );
}
 
export default BandeauBottom;
