import React, { useState, useEffect } from 'react'
import '../styles/BandeauBottom.css';
import BoutonAction from "./panels/BoutonAction"
import ForwardIcon from '@material-ui/icons/Forward';
import IconButton from '@material-ui/core/IconButton';
import Button from '@material-ui/core/Button'

import ContentsAPI from "../API/ContentsAPI"


const BandeauBottom = ({
    nbActions
    
}) => {
    
    const [textes, setTextes] = useState(null)

    useEffect(() => {
        console.log(textes)
        const getTextes = async () => {
            const textes = ContentsAPI.getTexts("jeuBandeauBottom").then(
                (res) => { setTextes(res) }
            )
        }
        if (textes == null) {
            getTextes()
        }
    }, [textes])


    const handleClicFleche=(num)=>{
        // const image from "./../assets/ExtincteurFeu.png"
        console.log(num)
    }

    const renderFleches = () => {
        return (
            <div className="fleches-container">
                 <IconButton 
                    className="fleche-up"
                    onClick={()=>handleClicFleche(0)}
                ><ForwardIcon 
                style={{color:"#4A4848"}} 
                stroke={"white"}
                strokeWidth={1} 
            />
                </IconButton>
                <IconButton 
                    className="fleche-droite"
                    onClick={()=>handleClicFleche(1)}
                >
                    <ForwardIcon 
                        style={{color:"#4A4848"}} 
                        stroke={"white"}
                        strokeWidth={1} 
                    />
                </IconButton>
                <div className="fleche-center">
                    <div className="circle-fleche"></div>
                </div>
                <IconButton 
                    className="fleche-bottom"
                    onClick={()=>handleClicFleche(2)}
                >
                    <ForwardIcon 
                        style={{color:"#4A4848"}} 
                        stroke={"white"}
                        strokeWidth={1}
                    />
                </IconButton>
                <IconButton 
                    className="fleche-gauche"
                    onClick={()=>handleClicFleche(3)}
                >
                    <ForwardIcon 
                        style={{color:"#4A4848"}} 
                        stroke={"white"}
                        strokeWidth={1} 
                    />
                </IconButton>

            </div>


        )
    }

    const renderButtons=()=>{
        var boutons=[]

        if(textes!=null){
            Object.keys(textes).forEach(label => {
                boutons.push(
                    <BoutonAction
                        action={textes[label].contenu}
                        nb={nbActions[label.replace("labelAction","")]}
                        sourceImage={"assets/"+label.replace("label","")+".png"}
                    />
                )
            });
        }
        return(
            <div className="boutons-container">
                {boutons}
            </div>
        )
    }

    const renderBoutonsValider=()=>{

        return(
            <div className="boutons-valider-container">
                <Button 
                    className="bouton-valider" 
                    variant='contained' 
                    disableElevation 
                    stroke={"white"}
                    strokeWidth={2} 
                >
                    <img src={"assets/validate.png"} alt={"image"}/>

                </Button>

                <Button className="bouton-valider" variant='contained' disableElevation>
                    <img src={"assets/cancel.png"} alt={"image"}/>
                </Button>

            </div>
        )

    }
    return (
        <div className="bandeau-bottom">
            {renderFleches()}
            
            
            {renderButtons()}

            {renderBoutonsValider()}

        </div>


    );
}

export default BandeauBottom;
