import React from 'react';
import { makeStyles } from '@material-ui/core/styles';
import CasePiece from "./CasePiece"



const Piece = ({
    carte,
    idPiece,
    position,
    nbLignes,
    nbColonnes,
    maxColonnesPiece,
    maxLignesPiece,
    mursV,
    mursH,
    anchor

}) => {

    const renderRow=(i)=>{
        var row=[]
        for(let j=0;j<nbColonnes;j++){
            const anchori = i+anchor[0]
            const anchorj = j+anchor[1]
            if (anchori<maxLignesPiece && anchorj<maxColonnesPiece){
                row.push( 
                    <CasePiece 
                        key={i+","+j}
                        murs={[mursH[anchori][anchorj],mursV[anchori][anchorj+1],mursH[anchori+1][anchorj],mursV[anchori][anchorj]]}
                        type={carte[anchori][anchorj]}
                        
                    />
                       
                )
            }
            
        }
        return row
    }


    const renderGrille=()=>{
        var grille=[]
        for (let i=0;i<nbLignes;i++){
            grille.push(
                <div key={"row"+i} className="row-piece">
                    {renderRow(i)}
                </div>
            )  
            
        }
        return grille
    }



    return (  
        <>
    <div
        className="piece"  
        style={{
            width:nbColonnes/maxColonnesPiece*80+"%",
            height:nbLignes/maxLignesPiece*100+"%"
        }} 
       
    >
       {renderGrille()}
      

    </div>
     <h1>Pièce numéro {idPiece}</h1>
    </>
        
    );
}
 
export default Piece;
