import React from 'react';
import { makeStyles } from '@material-ui/core/styles';

import './Grille.css';

import Piece from "./pieces/Piece"



const Grille = ({
    nbColonnes,
    nbLignes,
    carte,
    position,
    mursH,
    mursV
}) => {
    
    const maxLignesPiece = 8
    const maxColonnesPiece=16
    const anchor=[3,5]
    const nbLignes1=3+2
    const nbColonnes1=4+2
    return (  

    <div 
        className="piece-container"
    >
        <Piece
            mursH={mursH}
            mursV={mursV}
            carte={carte}
            // position={position}
            nbLignes={nbLignes1}
            nbColonnes={nbColonnes1}
            maxColonnesPiece={maxColonnesPiece}
            maxLignesPiece={maxLignesPiece}
            anchor={anchor}
        />
    </div>

    
    
        
        
    );
}
 
export default Grille;
