import React,{useState,useEffect} from 'react'
import CaseDonnee from './panels/CaseDonnee'
import '../styles/BandeauTop.css';

import ContentsAPI from "../API/ContentsAPI"

const BandeauTop = ({
    numeroPompier,
    cycle,
    tour,
    nbInterventions,
    nbExtFeu,
    nbExtFum,
    nbExtFeuFum,
    nbMarqueurs,
    nbDecedes,
    nbSauves

}) => {

    const [textes,setTextes]=useState(null)
    
    const renderFeu = ()=>{
    
        return (

            (tour%6)==numeroPompier ? 
            <button className="bouton-vert"></button>
            :
            <button className="bouton-rouge"></button>
        
        )
    }
    useEffect(()=>{
        console.log("use effect")
        const getTextes=async()=>{
            const textes = ContentsAPI.getTexts("jeuBandeauTop").then(
                (res)=>{setTextes(res)}
            )
        }
        getTextes()
    },[])

    const getText=(name)=>{
        if(textes==null){
            return name
        }else{
            return textes["label"+name.charAt(0).toUpperCase() + name.slice(1)].contenu
        }
    }


    return (  
        <div className="bandeau-top">
            <CaseDonnee
                name={getText("cycle")}
                value={cycle}
                
            />
            <CaseDonnee
                name={getText("tour")}
                value={tour}
            />
            <CaseDonnee
                name={getText("nbInterventions")}
                value={nbInterventions}
            />
            <CaseDonnee
                name={getText("nbExtFeu")}
                value={nbExtFeu}
            />
            <CaseDonnee
                name={getText("nbExtFeuFum")}
                value={nbExtFeuFum}
            />
            <CaseDonnee
                name={getText("nbExtFum")}
                value={nbExtFum}
            />
            <CaseDonnee
                name={getText("nbMarqueurs")}
                value={nbMarqueurs}
            />
            <CaseDonnee
                name={getText("nbDecedes")}
                value={nbDecedes}
            />
            <CaseDonnee
                name={getText("nbSauves")}
                value={nbSauves}
            />
            <CaseDonnee
                name={getText("numPompier")}
                value={numeroPompier}
            />
            {renderFeu()}
        </div>
       

    );
}
 
export default BandeauTop;
