import React, { useEffect, useState } from 'react';
import Grid from "@material-ui/core/Grid"
import Paper from "@material-ui/core/Paper"
import Table from '@material-ui/core/Table';
import TableBody from '@material-ui/core/TableBody';
import TableCell from '@material-ui/core/TableCell';
import TableContainer from '@material-ui/core/TableContainer';
import TableHead from '@material-ui/core/TableHead';
import TableRow from '@material-ui/core/TableRow';
import InputLabel from '@material-ui/core/InputLabel';
import MenuItem from '@material-ui/core/MenuItem';
import FormHelperText from '@material-ui/core/FormHelperText';
import FormControl from '@material-ui/core/FormControl';
import Select from '@material-ui/core/Select';

import '../../styles/Interfaces.css';


const InterventionsInter = ({

}) => {

    const nbPompiers = 6
    const numCycle = 2
    const cycles = [
        {
            numCycle: 1,
            interPomp1: 100,
            interPomp2: 200,
            interPomp3: 300,
            interPomp4: 400,
            interPomp5: 500,
            interPomp6: 600,
        },
        {
            numCycle: 2,
            interPomp1: 700,
            interPomp2: 800,
            interPomp3: 900,
            interPomp4: 1000,
            interPomp5: 1100,
            interPomp6: 1200,
        }
    ]



    const renderHeaders = () => {
        var components = []
        components.push(
            <TableCell key={0} align="center">Cycle</TableCell>
        )
        for (let i = 1; i <= nbPompiers; i++) {
            components.push(
                <TableCell key={i} align="center">Pompier {i}</TableCell>
            )
        }
        return (
            <TableRow>
            {components}
            </TableRow>
            )
    }

    const renderBudgets = () => {
        const cyclesSorted = cycles.sort((a, b) => a.numCycle - b.numCycle)
        var components = []
        cyclesSorted.forEach((cycle) => {
            var comp = []

            comp.push(
                <TableCell  scope="row" align="center" component="th">
                    Cycle nÂ°{cycle.numCycle}
                </TableCell>
            )

            Object.keys(cycle).forEach((key,index) => {
                if (key != "numCycle") {
                    comp.push(
                        <TableCell key={key} align="center">{renderSelect(true,index,cycle[key])}</TableCell>
                    )
                }
            })
            components.push(
                <TableRow hover key={cycle.numCycle}>
                    {comp}
                </TableRow>
            )
        })
        return (
            components
        )
    }

    const handleChangeSelect=(evt,newValue)=>{
        console.log(newValue)
    }


    const renderSelect=(disabled,numPompier,value)=>{
        var components = []
        components.push(
            <FormControl disabled={disabled} variant="outlined">
            <Select
            value={value}
            onChange={handleChangeSelect}
            displayEmpty
            inputProps={{ 'aria-label': 'Without label' }}
            >
            <MenuItem value="" disabled align="center">
                Budget {numPompier}
            </MenuItem>
            <MenuItem value={10} align="right">Ten</MenuItem>
            <MenuItem value={20}>Twenty</MenuItem>
            <MenuItem value={30}>Thirty</MenuItem>

            </Select >
          </FormControl>
        )
        return(
            components
        )
        
    }

    return (
        <div className="container">
            <TableContainer component={Paper} className="tableau">
                <Table stickyHeader aria-label="simple table">
                    <TableHead>
                        
                            {renderHeaders()}
                    </TableHead>
                    <TableBody>
                       {renderBudgets()}
                    </TableBody>
                </Table>
            </TableContainer>
        </div>
    )
}

export default InterventionsInter;
