import React, { useState, useEffect } from 'react'
import '../../styles/BandeauBottomInter.css';
import CaseDonnee from './panels/CaseDonnee'

import ContentsAPI from "../../API/ContentsAPI"


const BandeauBottomInter = ({
    numeroPompier,

    resultats,
}) => {

    const [textes, setTextes] = useState(null)

    useEffect(() => {
        // console.log(textes)
        // const getTextes = async () => {
        //     const textes = ContentsAPI.getTexts("jeuBandeauBottomInter").then(
        //         (res) => { setTextes(res) }
        //     )
        // }
        // if (textes == null) {
        //     getTextes()
        // }
    }, [textes])

    const getText = (name) => {
        if (textes == null) {
            return name
        } else {
            return textes["label" + name.charAt(0).toUpperCase() + name.slice(1)].contenu
        }
    }
   

    const renderResults = () => {   
            return (
                <div
                    className="case-panel donnees-container"
                    style={{
                        flex: 2
                    }}
                >
                    <label
                        className="label-title"
                    >
                        {getText("resultats") + " " + numeroPompier}
                    </label>
                    <CaseDonnee
                        key={4}
                        name={getText("nbDecedes")}
                        value={resultats.Decedes}
                        primary={false}
                        flexFactor={0}
                    />
                    <CaseDonnee
                        key={5}
                        name={getText("nbSauves")}
                        value={resultats.Sauves}
                        primary={false}
                        flexFactor={0}
                    />
                </div>
            )
        }
    return (
        <div className="bandeau-bottom">
            {renderResults()}
        </div>


    );
}

export default BandeauBottomInter;
