import React, { useState, useEffect } from 'react'
import '../styles/BandeauBottom.css';
import BoutonAction from "./panels/BoutonAction"
import ForwardIcon from '@material-ui/icons/Forward';
import IconButton from '@material-ui/core/IconButton';

import ContentsAPI from "../API/ContentsAPI"

import Image from "../assets/ExtincteurFeu.png"

const BandeauBottom = ({
    nbInterFeu,
    nbInterFumee,
    nbInterFeuFum,
    nbInterMarqueurs,
    nbInterOuvrirPorte,
    nbInterFermerPorte,
    nbInterHabitant
}) => {
    
    const [textes, setTextes] = useState(null)

    useEffect(() => {
        console.log(textes)
        const getTextes = async () => {
            const textes = ContentsAPI.getTexts("jeuBandeauBottom").then(
                (res) => { setTextes(res) }
            )
        }
        if (textes == null) {
            getTextes()
        }
    }, [textes])


    const handleClicFleche=(num)=>{
        // const image from "./../assets/ExtincteurFeu.png"
        console.log(num)
    }

    const renderFleches = () => {
        return (
            <div className="fleches-container">
                 <IconButton 
                    className="fleche-up"
                    onClick={()=>handleClicFleche(0)}
                ><ForwardIcon 
                style={{color:"#4A4848"}} 
                stroke={"white"}
                strokeWidth={1} 
            />
                </IconButton>
                <IconButton 
                    className="fleche-droite"
                    onClick={()=>handleClicFleche(1)}
                >
                    <ForwardIcon 
                        style={{color:"#4A4848"}} 
                        stroke={"white"}
                        strokeWidth={1} 
                    />
                </IconButton>
                <div className="fleche-center">
                    <div className="circle-fleche"></div>
                </div>
                <IconButton 
                    className="fleche-bottom"
                    onClick={()=>handleClicFleche(2)}
                >
                    <ForwardIcon 
                        style={{color:"#4A4848"}} 
                        stroke={"white"}
                        strokeWidth={1}
                    />
                </IconButton>
                <IconButton 
                    className="fleche-gauche"
                    onClick={()=>handleClicFleche(3)}
                >
                    <ForwardIcon 
                        style={{color:"#4A4848"}} 
                        stroke={"white"}
                        strokeWidth={1} 
                    />
                </IconButton>

            </div>


        )
    }


    return (
        <div className="bandeau-bottom">
            {renderFleches()}
            <div className="boutons-container">
                
                <BoutonAction
                    action="Eteindre un feu"
                    nb={nbInterFeu}
                />
                <BoutonAction
                    action="Atténuer le feu en fumée"
                    nb={nbInterFeuFum}
                />
                <BoutonAction
                    action="Stopper la fumée"
                    nb={nbInterFumee}
                />
                <BoutonAction
                    action="Détruire porte/mur"
                    nb={nbInterMarqueurs}
                />
                <BoutonAction
                    action="Ouvrir la porte"
                    nb={nbInterOuvrirPorte}
                />
                <BoutonAction
                    action="Fermer la porte"
                    nb={nbInterFermerPorte}
                />
                <BoutonAction
                    action="Transporter un habitant"
                    nb={nbInterHabitant}
                />
            </div>

        </div>


    );
}

export default BandeauBottom;
