import React from 'react';
import { makeStyles } from '@material-ui/core/styles';

import './Grille.css';

import Piece from "./pieces/Piece"



const Grille = ({
    nbColonnes,
    nbLignes,
    carte,
    position,
    mursH,
    mursV
}) => {

    const getAnchors=()=>{   
        var anchors = []
        for(let i=0;i<mursV.length;i++){
            for(let j=0;j<mursH[i].length;j++){
                if(mursV[i][j]>0 && mursH[i][j]>0){
                    anchors.push([i,j])
                }   
            }
        }
        return anchors
    }

    const getAnchor=(anchors,pos)=>{
        var anchor =[0,0]
        anchors.forEach(a => {
            if(a[0]>=anchor[0]  && a[0]<=pos[0] && a[1]>=anchor[1] && a[1]<=pos[1]){
                anchor=a
            }
        });
        console.log("anchor => "+anchor+" : pos => "+pos)
        return anchor
    }

    const maxLignesPiece = 8
    const maxColonnesPiece=16
    const anchors=getAnchors()
    const anchor=getAnchor(anchors,position) // row, column
    const nbLignes1=8
    const nbColonnes1=16



    return ( 
        <div 
            className="piece-container"
        >
            <Piece
                mursH={mursH}
                mursV={mursV}
                carte={carte}
                position={position}
                nbLignes={nbLignes1}
                nbColonnes={nbColonnes1}
                maxColonnesPiece={maxColonnesPiece}
                maxLignesPiece={maxLignesPiece}
                anchor={[0,0]}
            />
        </div>
    )
}
 
export default Grille;
