import io from 'socket.io-client'

const urlBack = "http://localhost:4000";
const socket = io.connect(urlBack)

export default {
   
    nouveauTour:async(numPompier,carte,mursH,mursV,session,equipe)=>{
        socket.emit(
            'changementTour',
            {
                numPompier :parseInt(numPompier),
                carte:carte,
                mursH:mursH,
                mursV:mursV,
                session:session,
                equipe:equipe
            }
            
        )
    },
    getSocket:()=>{
        return socket
    },
    getConfiguration:async(session)=>{
        const requestOptions = {
            method: 'POST',
            headers: { 'Content-Type': 'application/json' },
            body: JSON.stringify({session:session})
        };
        const response = await  fetch(urlBack+"/configuration", requestOptions)
        return  await response.json()
    },
    getPlayState:async(session,equipe)=>{
        const requestOptions = {
            method: 'POST',
            headers: { 'Content-Type': 'application/json' },
            body: JSON.stringify({
                session:session,
                equipe:equipe
            })
        };
        const response = await  fetch(urlBack+"/playstate", requestOptions)
        return  await response.json()
    }
};
