const urlBack = "http://localhost:4000";

export default {
    companies: async(input,els)=>{
        const requestOptions = {
            method: 'POST',
            headers: { 'Content-Type': 'application/json' },
            body: JSON.stringify({input:input,els:els})
        };

       
        const response = await  fetch(urlBack+"/companies", requestOptions)
        const data = await response.json();
          
        return data
    }
};
