import React from 'react';
import { makeStyles } from '@material-ui/core/styles';
import List from '@material-ui/core/List';
import ListItem from '@material-ui/core/ListItem';
import ListItemText from '@material-ui/core/ListItemText';
import ListSubheader from '@material-ui/core/ListSubheader';

const useStyles = makeStyles((theme) => ({
    root: {
      width: '100%',
      maxWidth: 360,
      backgroundColor: theme.palette.background.paper,
      position: 'relative',
      overflow: 'auto',
      maxHeight: 300,
    },
    listSection: {
      backgroundColor: 'inherit',
    },
    ul: {
      backgroundColor: 'inherit',
      padding: 0,
    },
  }));
  
const ListCompanies=({
    companies,
    input
})=>{

    const classes = useStyles();




    const handleClickItem =(index)=>{
        console.log("Clic item "+index)
    }

    return (
        <List className={classes.root} >
            {companies.map((comp,index)=>{

              const indexStr = comp.name.toUpperCase().indexOf(str.toUpperCase())

              return(
                <ListItem button key={index} onClick={()=>handleClickItem(index)}>
                <ListItemText > {comp.name.substring(0, indexStr)}
                  <strong>
                      {comp.name.substring(indexStr, indexStr+str.length)}
                  </strong>
                  {comp.name.substring(indexStr+str.length, comp.name.length)}
                </ListItemText>
              </ListItem>
              )
               
            }
            
            
            )}
           
            
        </List>

    )
}

export default ListCompanies
