import React,{useState,useEffect} from 'react'
import CaseDonnee from './panels/CaseDonnee'
import '../styles/BandeauTop.css';

import ContentsAPI from "../API/ContentsAPI"

const BandeauTop = ({
    numeroPompier,
    cycle,
    tour,
    nbInterventions,
    nbExtFeu,
    nbExtFum,
    nbExtFeuFum,
    nbMarqueurs,
    nbDecedes,
    nbSauves

}) => {

    const [textes,setTextes]=useState(null)
    
    const renderFeu = ()=>{
    
        return (

            (tour%6)==numeroPompier ? 
            <button className="bouton-vert"></button>
            :
            <button className="bouton-rouge"></button>
        
        )
    }
    useEffect(()=>{
        console.log("use effect")
        const getTextes=async()=>{
            const textes = ContentsAPI.getTexts("jeuBandeauTop").then(
                (res)=>{setTextes(res)}
            )
        }
        getTextes()
    },[])

    const getText=(name)=>{
        if(textes==null){
            return name
        }else{
            return textes["label"+name.charAt(0).toUpperCase() + name.slice(1)].contenu
        }
    }

    const renderStocks=()=>{
        var components=[]
        components.push(
        <label 
            className="label-title"
        >
            {getText("stockEquipements")}
        </label>)



            

        return (
            <div 
            className="case-panel"

            style={{flexDirection:"column"}}
            >
            <CaseDonnee
                name={getText("nbInterventions")}
                value={nbInterventions}
                primary={true}
            />
            <CaseDonnee
                name={getText("nbExtFeu")}
                value={nbExtFeu}
                primary={false}
            />
            <CaseDonnee
                name={getText("nbExtFeuFum")}
                value={nbExtFeuFum}
                primary={false}
            />
            <CaseDonnee
                name={getText("nbExtFum")}
                value={nbExtFum}
                primary={false}
            />
            <CaseDonnee
                name={getText("nbMarqueurs")}
                value={nbMarqueurs}
                primary={false}
            />
            </div>
            



        )

    }


    return (  
        <div className="bandeau-top">
            <CaseDonnee
                name={getText("cycle")}
                value={cycle}
                primary={true}
                
            />
            <CaseDonnee
                name={getText("tour")}
                value={tour}
                primary={true}
            />
           
            <CaseDonnee
                name={getText("nbDecedes")}
                value={nbDecedes}
                primary={false}
            />
            <CaseDonnee
                name={getText("nbSauves")}
                value={nbSauves}
                primary={false}
            />
            <CaseDonnee
                name={getText("numPompier")}
                value={numeroPompier}
                primary={true}
            />
            {renderFeu()}
        </div>
       

    );
}
 
export default BandeauTop;
