import React,{useState,useEffect} from 'react'
import io from 'socket.io-client'
import TextField from '@material-ui/core/TextField'

const socket = io.connect('http://localhost:4000')

function App() {
  const [state,setState]=useState({message:"",name:""})

  const [numPompier,setNumPompier]=useState(null)

  const [chronologie,setChronologie]=useState([])

  const renderTour = ()=>{
    return chronologie.map(({numPompier,message},index)=>(
      <div key={index}>
        <h3>{numPompier} : <span>{message}</span></h3>
      </div>
    ))
  }


  const onMessageSubmit=(e)=>{
    e.preventDefault()
    const {name,message}=state
    socket.emit('message',{name,message})
    setState({message:"",name})
  }


  useEffect(() => {
    socket.on('message',({name,message})=>{
      setChat([...chat,{name,message}])
    })
  });


  return (
    <div className="card">
      <form  onSubmit={onMessageSubmit}>
        <h1>Messenger</h1>
        <div className="name-field">
          <TextField
            name="numPompier"
            onChange={e=>onTextChange(e)}
            value={numPompier}
            label="NumÃ©ro pompier"
          />
        </div>
        <div className="name-field">
          <TextField
            name="message"
            onChange={e=>onTextChange(e)}
            value={state.message}
            label="Message"
          />
          <input type='hidden' value={numPompier} name="numPompier"/>
        </div>
        <button>Fin du tour</button>
      </form>
      <div className='render-chat'>


        <h1>Chat log</h1>
        {renderChat()}
      </div>
    </div>
  );
}

export default App;
