import { withWidth } from '@material-ui/core';
import zIndex from '@material-ui/core/styles/zIndex';
import React,{useState,useEffect} from 'react'

const MurPiece = ({
    num,
    height,
    width,
    type,
    coordX,
    coordY,
    maxColonnesPiece,
    maxLignesPiece
}) => {
    const epaisseurInit=1
    const epaisseur=type>0 ? type==3 ? epaisseurInit*8 : epaisseurInit*4 : 1
    
    // console.log(type)
    
    const colors =[
        "", // 0 simple
        "black", // 1 large
        "purple",//2 porte closed
        "purple",//3 porte open
    ]

   const getTransform=()=>{
        var facteur = 1
        if(coordX<=maxColonnesPiece/2-1){
            facteur=-1
        }
        const translateY=facteur*epaisseur/1.5
        const translateX=facteur*width*0.9
        if(num==0 && type==3) return 'translate('+translateX+"px,"+translateY+"px)"
        if(num==2 && type==3) return 'translate('+translateX+"px,"+(-translateY)+"px)"
   }

   
   const test=()=>{
       console.log(coordX+" "+maxColonnesPiece)
       console.log(coordY+" "+maxLignesPiece)
   }

    return ( 
        <div       
            onClick={()=>test()}  
            style={{
                position:"absolute",
                width:num%2==0 ? width+2 : epaisseur,
                height:num%2==1 ? height+1 :  epaisseur , 
                right:num==1 ? -1 :  null,
                left:num%2==0 || num==3 ? -1 : null,
                top:num==0 || num%2==1 ? -1 : null,
                bottom:num==2 ? 0 :  null,
                backgroundColor:(type>0) ? colors[type] : 'transparent',
                zIndex: type==2 || type==3 ? 2 : 1,
                transform: type==3 ? getTransform() : ''
            }}
        >
        </div>

    );
}
 
export default MurPiece;
