import React from 'react';
import { makeStyles } from '@material-ui/core/styles';
import CaseGrille from "../CaseGrille"
import MurGrille from "../MurGrille"
import Grid from '@material-ui/core/Grid';

import '../Grille.css';




const Piece = ({
    carte,
    position,
    nbLignes,
    nbColonnes,
    maxColonnesPiece,
    maxLignesPiece

}) => {


    const renderGrille=()=>{
        var grille=[]
        for (let i=0;i<nbLignes;i++){
             for(let j=0;j<nbColonnes;j++){
                grille.push( 
                    <CaseGrille 
                        key={i+","+j}
                        murs={[0,1,2,3]}
                        type={carte[i+position[1]][j+position[0]]}
                        // xs={8/nbColonnes  }
                    />
                       
                )
             }
            
        }
        return grille
    }



    return (  

    <Grid  
        container 
        
        className="piece"   
        xs={8} 
        // style={{width:nbColonnes/maxColonnesPiece*100+"%"}} 
        direction="column"
    >
       {renderGrille()}
    </Grid>
        
        
    );
}
 
export default Piece;
