import React,{useState,useEffect} from "react";

// import { Button, FormGroup, FormControl, ControlLabel } from "react-bootstrap";
import { TextField,FormControlLabel,Checkbox } from '@material-ui/core'
import API from "../API/API";

const SearchField = ({
    handlerSearch,
    selectedCompany
})=> {

    const [input,setInput]=useState(selectedCompany)
    const [els,setEls]=useState(false)

    const [companies,setCompanies]=useState([])

    const getData = async()=> {

        const data =  await API.companies(input,els)
        if (data!=companies){
            setCompanies(data)
        }
       
    }

    useEffect(()=>{
        getData()
    },[input,els])
    
   
    useEffect(()=>{
        handlerSearch(companies,input)
    },[companies])
 
    const onTextChange = (e)=>{
        e.preventDefault()
        setInput(e.target.value)
    }

    const onCheckChange=(e)=>{
        e.preventDefault()
        setEls(e.target.checked)
    }

    return(
        <div className="search-field">
        <TextField 
            id="standard-basic" 
            label={"Company name"}
            onChange={e=>onTextChange(e)} 
            variant="outlined"
            value={input}
        
            //helperText="Please enter a company name"
        />

        <FormControlLabel
            control={
            <Checkbox
                // checked={state.checkedB}
                onChange={e=>onCheckChange(e)}
                name="ELS"
                color="primary"
            />
            }
            label="ELS"
        />
        </div>
    )
}

export default SearchField
