import React, { useState, useEffect } from 'react'
import CaseDonnee from './panels/CaseDonnee'
import '../styles/BandeauTop.css';

import ContentsAPI from "../API/ContentsAPI"

const BandeauTop = ({
    numeroPompier,
    cycle,
    tour,
    nbInterventions,
    nbExtFeu,
    nbExtFum,
    nbExtFeuFum,
    nbMarqueurs,
    nbDecedes,
    nbSauves

}) => {

    const [textes, setTextes] = useState(null)

    const renderFeu = () => {

        return (

            (tour % 6) == numeroPompier ?
                <button className="bouton-vert"></button>
                :
                <button className="bouton-rouge"></button>

        )
    }
    useEffect(() => {
        console.log("use effect")
        const getTextes = async () => {
            const textes = ContentsAPI.getTexts("jeuBandeauTop").then(
                (res) => { setTextes(res) }
            )
        }
        getTextes()
    }, [])

    const getText = (name) => {
        if (textes == null) {
            return name
        } else {
            return textes["label" + name.charAt(0).toUpperCase() + name.slice(1)].contenu
        }
    }

    const renderStocks = () => {
        return (
            <div
                className="case-panel donnees-container"
            >
                <label
                    className="label-title"
                >
                    {getText("stockEquipements")}
                </label>
                <div className="sous-donnees-container">
                    <CaseDonnee
                        name={getText("nbExtFeu")}
                        value={nbExtFeu}
                        primary={false}
                        flexFactor={2}
                    />
                    <CaseDonnee
                        name={getText("nbExtFum")}
                        value={nbExtFum}
                        primary={false}
                        flexFactor={2}
                    />
                </div>
                <div className="sous-donnees-container">
                    <CaseDonnee
                        name={getText("nbExtFeuFum")}
                        value={nbExtFeuFum}
                        primary={false}
                        flexFactor={2}
                    />
                    <CaseDonnee
                        name={getText("nbMarqueurs")}
                        value={nbMarqueurs}
                        primary={false}
                        flexFactor={2}
                    />
                </div>
            </div>
        )
    }

    const renderResults=()=>{
        return (
            <div
                className="case-panel donnees-container"
                style={{
                    flex:3
                }}
            >
                <label
                    className="label-title"
                    style={{
                        flex:1
                    }}
                >
                    {getText("resultats")}
                </label>
                <CaseDonnee
                    name={getText("nbDecedes")}
                    value={nbDecedes}
                    primary={false}
                    flexFactor={1}
                />
                <CaseDonnee
                    name={getText("nbSauves")}
                    value={nbSauves}
                    primary={false}
                    flexFactor={1}
                />
            </div>
        )
    }


    return (
        <div className="bandeau-top">
            <CaseDonnee
                name={getText("cycle")}
                value={cycle}
                primary={true}
                flexFactor={1}

            />
            <CaseDonnee
                name={getText("tour")}
                value={tour}
                primary={true}
                flexFactor={1}
            />
            <CaseDonnee
                name={getText("nbInterventions")}
                value={nbInterventions}
                primary={true}
                flexFactor={1}
            />
            {renderStocks()}
            {renderResults()}
            <CaseDonnee
                name={getText("numPompier")}
                value={numeroPompier}
                primary={true}
                flexFactor={1}
            />
            {renderFeu()}
        </div>


    );
}

export default BandeauTop;
