import './App.css';
import React,{useState,useEffect} from 'react'
import io from 'socket.io-client'
import TextField from '@material-ui/core/TextField'

const socket = io.connect('http://localhost:4000')

function App() {

  const [numPompier,setNumPompier]=useState(0)

  const [chronologie,setChronologie]=useState([])

  const renderTour = ()=>{
    console.log(numPompier)
    return chronologie.map(({num,message},index)=>(
      <div key={index}>
        {num==numPompier ? 
        <h3>{num} : <span>{message}</span></h3>

        :
        <h3>{num} : <span>{"Ce n'est pas à toi de jouer !"+numPompier}</span></h3>
        }
      </div>
    ))
  }

  const onTextChange=(e)=>{
    setNumPompier(e.target.value)
    console.log(e.target.value)
  }


  const onTourChange=(e)=>{
    e.preventDefault()
    const message="C'est ton tour, à toi d'effectuer les actions"
    socket.emit('tour',{numPompier,message})
  }


  useEffect(() => {
    socket.on('tour',({numPompier,message})=>{
      setChronologie([...chronologie,{numPompier,message}])
    })
  });


  return (
    <div className="card">
      <form  onSubmit={e=>onTourChange(e)}>
        <h1>Messenger</h1>
        <div className="name-field">
          <TextField
            name="numPompier"
            onChange={e=>onTextChange(e)}
            value={numPompier}
            label="Numéro pompier"
          />
        </div>

        <input type='hidden' value={numPompier} name="numPompier"/>

        <button>Fin du tour</button>
      </form>
      <div className='render-chat'>


        <h1>Chronologie log</h1>
        {renderTour()}
      </div>
    </div>
  );
}

export default App;
