import React, { useEffect, useState, useRef } from 'react';
import Grid from "@material-ui/core/Grid"
import Paper from "@material-ui/core/Paper"
import Table from '@material-ui/core/Table';
import TableBody from '@material-ui/core/TableBody';
import TableCell from '@material-ui/core/TableCell';
import TableContainer from '@material-ui/core/TableContainer';
import TableHead from '@material-ui/core/TableHead';
import TableRow from '@material-ui/core/TableRow';
import InputLabel from '@material-ui/core/InputLabel';
import MenuItem from '@material-ui/core/MenuItem';
import FormHelperText from '@material-ui/core/FormHelperText';
import FormControl from '@material-ui/core/FormControl';
import Select from '@material-ui/core/Select';
import TextField from '@material-ui/core/TextField';

import '../../styles/Interfaces.css';


const InterInterventions = ({
    budget,
    cycles,
    handleBudgetChange,
    type,
    currentCycle
}) => {
    const lastRow = useRef(null)

    const nbPompiers = 6

    const [numCycle, setNumCycle] = useState(1)
    const [lastCycle, setLastCycle] = useState(
        {
            numSeq:1,
            numCycle: cycles.length+1,
            pomp1: 0,
            pomp2: 0,
            pomp3: 0,
            pomp4: 0,
            pomp5: 0,
            pomp6: 0,
        }
    )



    const renderHeaders = () => {
        var components = []
        components.push(
            <TableCell key={0} align="center" component="th">Séquence</TableCell>
        )
        components.push(
            <TableCell key={0} align="center" component="th">Cycle</TableCell>
        )
        for (let i = 1; i <= nbPompiers; i++) {
            components.push(
                <TableCell key={i} align="center" component="th">Pompier {i}</TableCell>
            )
        }
        components.push(
            <TableCell key={1000} align="center" component="th">Total</TableCell>
        )
        return (
            <TableRow>
                {components}
            </TableRow>
        )
    }


    const styles = {
        root: {
            background: "black"
        },
        input: {
            color: "white"
        }
    };


    const renderRow = (cycle) => {
        console.log(cycle.numCycle)
        if(cycle.numCycle<currentCycle+2){
            var comp = []
            var sum = 0
            comp.push(
                <TableCell scope="row" align="center" component="th" >
                    Séquence n°{cycle.numSeq}
                </TableCell>
            )
            comp.push(
                <TableCell scope="row" align="center" component="th" >
                    Cycle n°{cycle.numCycle}
                </TableCell>
            )
    
            Object.keys(cycle).forEach((key, index) => {
                if (key != "numCycle" && key!="numSeq") {
                    comp.push(
                        <TableCell key={key} align="center">{renderSelect(cycle["numCycle"], numCycle != cycle["numCycle"], index-1, cycle[key])}</TableCell>
                    )
                    sum += cycle[key]
                }
            })
            comp.push(
                <TableCell scope="row" align="center"  >
                    <TextField
                        id="outlined-basic"
                        variant="outlined"
                        disabled
                        value={sum + " €"}
                        inputProps={{
                            style: {
                                color: numCycle == cycle["numCycle"] ? sum > budget.sequence.total - budget.sequence.depense ? "red" : "green" : null,
                                fontWeight: numCycle == cycle["numCycle"] ? "bold" : null,
                                fontStyle: numCycle != cycle["numCycle"] ? "italic" : null,
                                textAlign: "center",
                                backgroundColor: "#f1f1f1",
                                borderRadius: 5,
                                minWidth: 70
    
                            },
                        }}
                    />
    
    
                </TableCell>
            )
            return (
                <TableRow hover key={cycle.numCycle} ref={cycle.numCycle>=currentCycle ? lastRow : null}>
                    {comp}
                </TableRow>
            )

        }else{
            return null
        }
    }
    const renderBudgetsCycles = () => {
        if(cycles.length+1!==lastCycle.numCycle) initLastCycle()
        const cyclesSorted = cycles.sort((a, b) => a.numCycle - b.numCycle)
        var components = []
        cyclesSorted.forEach((cycle) => {

            components.push(
                renderRow(cycle)
            )

        })
        components.push(renderRow(lastCycle))
        return (
            components
        )
    }

    const handleChangeSelect = (numPompier, event) => {
        var c = {...lastCycle}
        c["pomp" + numPompier] = event.target.value
        setLastCycle(c)
        handleBudgetChange(type,c)
        console.log(numPompier,c)
    }

    const initLastCycle=()=>{
        setLastCycle(
            {
                numSeq:1,
                numCycle: cycles.length+1,
                pomp1: 0,
                pomp2: 0,
                pomp3: 0,
                pomp4: 0,
                pomp5: 0,
                pomp6: 0,
            }
        )
    }
    

    useEffect(() => {
        // console.log(lastCycle)
        setNumCycle(cycles.length+1)
        lastRow.current.scrollIntoView()
    }, [lastCycle])


    const renderPropositionsBudget = (nCycle, numPompier) => {
        
        let cycle = cycles.sort((a, b) => a.numCycle - b.numCycle)[nCycle - 1]
        if(cycle==null && nCycle>cycles.length) cycle=lastCycle
        let sum = 0
        if(cycle!=null){
            Object.keys(cycle).forEach((key, index) => {
                if (key != "numCycle" && key!=="numSeq" && (numPompier !== index-1 || nCycle<numCycle) ) {
                    sum += cycle[key]
                }
            })
        }
        // console.log(nCycle)
       
        var components = []
        const maxValue =cycle!=null && nCycle<numCycle ?  Math.floor((sum ) / 100) : Math.floor((budget.sequence.total - budget.sequence.depense - sum) / 100)
        
        // console.log(nCycle,maxValue,cycle,sum)

        for (let i = 0; i <=maxValue; i++) {
            components.push(
                <MenuItem value={i * 100} align="right" >{i * 100 + "€"}</MenuItem>
            )
        }
        return components
    }

    const renderSelect = (nCycle, disabled, numPompier, value) => {
        var components = []
        components.push(
            <FormControl disabled={disabled} variant="outlined">
                <Select
                    value={value}
                    onChange={(value) => handleChangeSelect(numPompier, value)}
                    // displayEmpty
                    color="secondary"
                >
                    <MenuItem value="" disabled align="center">
                        Budget {numPompier}
                    </MenuItem>

                    {renderPropositionsBudget(nCycle, numPompier)}

                </Select >
            </FormControl>
        )
        return (
            components
        )

    }


    return (
        <div className="container">
            <TableContainer component={Paper} className="tableau">
                <Table stickyHeader aria-label="simple table">
                    <TableHead>
                        {renderHeaders()}
                    </TableHead>
                    <TableBody>
                        {renderBudgetsCycles()}
                    </TableBody>
                </Table>
            </TableContainer>
        </div>
    )
}

export default InterInterventions;
