import { createMuiTheme } from '@material-ui/core/styles';

const MyTheme = createMuiTheme({
    palette: {
      primary: {
        main:"#F02000",
      },
      secondary: {
        main: '#11cb5f',
      },
     
    },

    typography: {
      fontFamily: 'Raleway, Arial',
    },
   
  });

  MyTheme.typography.h3 = {
    fontSize: '1.2rem',
    '@media (min-width:600px)': {
      fontSize: '1.5rem',
    },
    [theme.breakpoints.up('md')]: {
      fontSize: '2.4rem',
    },
  };
export default MyTheme
