import React from 'react';
import { makeStyles } from '@material-ui/core/styles';
import CasePiece from "./CasePiece"
import Grid from '@material-ui/core/Grid';

import '../Grille.css';




const Piece = ({
    carte,
    position,
    nbLignes,
    nbColonnes,
    maxColonnesPiece,
    maxLignesPiece

}) => {

    const renderRow=(i)=>{
        var row=[]
        for(let j=0;j<nbColonnes;j++){
            row.push( 
                <CasePiece 
                    key={i+","+j}
                    murs={[0,1,2,3]}
                    type={carte[i+position[1]][j+position[0]]}
                    // width={1/nbColonnes*100}
                    // height={1/nbLignes*100}
                    
                />
                   
            )
        }
        return row
    }


    const renderGrille=()=>{
        var grille=[]
        for (let i=0;i<nbLignes;i++){
            grille.push(
                <div className="row-piece">
                    {renderRow(i)}
                </div>
            )  
            
        }
        return grille
    }



    return (  

    <div
        className="piece"  
        style={{
            width:nbColonnes/maxColonnesPiece*80+"%",
            height:nbLignes/maxLignesPiece*100+"%"
        }} 
       
    >
       {renderGrille()}
    </div>
        
        
    );
}
 
export default Piece;
