import axios from "axios";
const headers = {
  "Content-Type": "application/json"
};
const urlBack = "http://localhost:4000";

export default {
    companies: function(str){
        const data= axios.post(
            urlBack+"/companies",
            {
                str
            },
            { 
               headers: headers
            }
        )
        .then( (response) => (response.data))
        console.log(data)
        return data
    }
};
