import React from 'react';
import { makeStyles } from '@material-ui/core/styles';
import CasePiece from "./CasePiece"



const Piece = ({
    carte,
    idPiece,
    position,
    nbLignes,
    nbColonnes,
    maxColonnesPiece,
    maxLignesPiece,
    mursV,
    mursH,
    anchor

}) => {
    console.log("Piece nb lignes : "+nbLignes+" : "+nbColonnes)
    const renderRow=(i)=>{
        var row=[]
        for(let j=0;j<nbColonnes+2;j++){
            const anchori = i+anchor[0]-1
            const anchorj = j+anchor[1]-1
            
            if (anchori<=nbLignes &&  anchori>=0 && anchorj<=nbColonnes &&  anchorj>=0 ){
                console.log("créé : ",anchori , anchorj)
                row.push( 
                    <CasePiece 
                        key={i+","+j}
                        coordX={j}
                        coordY={i}
                        maxColonnesPiece={maxColonnesPiece}
                        maxLignesPiece={maxLignesPiece}
                        murs={[mursH[anchori][anchorj],mursV[anchori][anchorj+1],mursH[anchori+1][anchorj],mursV[anchori][anchorj]]}
                        type={ j>nbColonnes || i>nbLignes ? 0 : carte[anchori][anchorj] }
                        
                    />
                )
            }else{
               
                let newI = anchori
                let newJ = anchorj

                if(anchori<0  ){
                    console.log("annulé : ",anchori , anchorj)
                    newI=0
                    row.push( 
                        <CasePiece 
                            key={i+","+j}
                            coordX={anchorj}
                            coordY={newI}
                            maxColonnesPiece={maxColonnesPiece}
                            maxLignesPiece={maxLignesPiece}
                            murs={[
                                mursH[newI][newJ]>1 ? mursH[newI][newJ] : 0,
                                mursV[newI][newJ+1]>1 ? mursH[newI][newJ+1] : 0,
                                mursH[newI+1][newJ]>1 ? mursH[newI+1][newJ] : 0,
                                mursV[newI][newJ]>1 ? mursH[newI][newJ] : 0
                            ]}
                            type={0}
                            
                        /> 
                    )
                }

            }
            
        }
        return row
    }


    const renderGrille=()=>{
        var grille=[]
        for (let i=0;i<nbLignes+2;i++){
            grille.push(
                <div key={"row"+i} className="row-piece" >
                    {renderRow(i)}
                </div>
            )  
            
        }
        return grille
    }



    return (  

    <div
        className="piece"  
        style={{
            position:"absolute",
            width:nbColonnes/maxColonnesPiece*80+"%",
            height:nbLignes/maxLignesPiece*100+"%",
            
        }} 
       
    >
       {renderGrille()}
    </div>
        
        
    );
}
 
export default Piece;
