import { withWidth } from '@material-ui/core';
import zIndex from '@material-ui/core/styles/zIndex';
import React, { useState, useEffect } from 'react'

const MurPiece = ({
    num,
    height,
    width,
    type,
    coordX,
    coordY,
    maxColonnesPiece,
    maxLignesPiece
}) => {
    const epaisseurInit = 1
    const epaisseur = type > 0 ? type == 3 ? epaisseurInit * 8 : epaisseurInit * 4 : 1

    // console.log(type)

    const colors = [
        "", // 0 simple
        "black", // 1 large
        "purple",//2 porte closed
        "purple",//3 porte open
    ]

    const getTransform = () => {
        var facteurX = 1
        if (coordX <= maxColonnesPiece / 2 -4) {
            facteurX = -1
        }
      
        const translateX = facteurX * width * 0.9
        const translateY = facteurX * epaisseur / 1.5
        if (num == 0 && type == 3) return 'translate(' + translateX + "px," + translateY + "px)"
        if (num == 2 && type == 3) return 'translate(' + translateX + "px," + (translateY) + "px)"

      

    }

    const getOpacity=()=>{
        // if (coordX <= maxColonnesPiece /2 -3  && type==3 && num==2) {
        //     return 0
        // }
        return 1
    }


    const test = () => {
        console.log("coords X "+coordX + " " + maxColonnesPiece)
        console.log("coords Y "+coordY + " " + maxLignesPiece)
    }

    return (
        <div
            onClick={() => test()}
            style={{
                position: "absolute",
                width: num % 2 == 0 ? width + 2 : epaisseur,
                height: num % 2 == 1 ? height + 1 : epaisseur,
                right: num == 1 ? -1 : null,
                left: num % 2 == 0 || num == 3 ? -1 : null,
                top: num == 0 || num % 2 == 1 ? -1 : null,
                bottom: num == 2 ? 0 : null,
                backgroundColor: (type > 0) ? colors[type] : 'transparent',
                zIndex: type == 2 || type == 3 ? 2 : 1,
                transform: type == 3 ? getTransform() : '',
                opacity:getOpacity()
            }}
        >
        </div>

    );
}

export default MurPiece;
