import { withWidth } from '@material-ui/core';
import React,{useState,useEffect} from 'react'

const MurGrille = ({
    type,
    position,
    vertical
}) => {
    
    const colorMur="black"

    return ( 
       <div 
            className="mur" 
            style={{
               
                backgroundColor:"black",
                width:vertical ? 10 : "100%",
                height:vertical ?  "100%" : 10,
                borderWidth:5,
                borderColor:"black",
                marginTop:position==2 ? "auto" : null,
                marginBottom:position==2 ? 0 : null
            }}>
       </div>

    );
}
 
export default MurGrille;
