import React, { useState, useEffect } from 'react'
import "../.././styles/BandeauBottom.css"
import BoutonAction from "./panels/BoutonAction"
import ForwardIcon from '@material-ui/icons/Forward';
import IconButton from '@material-ui/core/IconButton';
import Button from '@material-ui/core/Button'

import { CSSTransitionGroup } from "react-transition-group"
import ContentsAPI from "../../API/ContentsAPI"


const BandeauBottom = ({
    interventions,
    stocks,
    numeroPompier,
    tour,
    handleClicFleche,
    handleClicValidated,
    handleClicDoor,
    handleClicMarqueur,
    handleClicExt,
    handleNoClicked,
    handleClicHabitant,
    btnClickedBottom,
    mursAutour,
    casesAutour,
    currentCase

}) => {
    const [textes, setTextes] = useState(null)
    useEffect(() => {
        const getTextes = async () => {
            const textes = ContentsAPI.getTexts("jeuBandeauBottom").then(
                (res) => { setTextes(res) }
            )
        }
        if (textes == null) {
            getTextes()
        }
    }, [textes])


    const handleClicAction = (num) => {
        if (num == null) {
            handleNoClicked()
        } else {
            if (num == 4 || num == 5) handleClicDoor(num == 4)
            if (num == 3) handleClicMarqueur()
            if (num < 3) handleClicExt(num)
            if (num == 6) handleClicHabitant()
        }


    }

    const renderFleches = () => {
        const disabled = interventions.Deplacement <=1
        console.log(disabled,interventions.Deplacement)
        const color = "#4A4848"
        const colorDisabled = "#BFBFBF"
        const mursInterdits = [1, 2, 3, 5]
        return (
            <div className="fleches-container">
                <IconButton
                    key={0}
                    className="fleche-up fleche"
                    onClick={() => handleClicFleche(0)}
                    disabled={disabled || mursInterdits.includes(mursAutour[0]) || casesAutour.dessus > 0}
                >
                    <ForwardIcon
                        style={{ color: disabled || mursInterdits.includes(mursAutour[0]) || casesAutour.dessus > 0 ? colorDisabled : color }}
                        stroke={"white"}
                        strokeWidth={1}
                    />
                </IconButton>
                <IconButton
                    key={1}
                    className="fleche-droite fleche"
                    onClick={() => handleClicFleche(1)}
                    disabled={disabled || mursInterdits.includes(mursAutour[1]) || casesAutour.droite > 0}
                >
                    <ForwardIcon
                        // style={{ color: "#4A4848" }}
                        style={{ color: disabled || mursInterdits.includes(mursAutour[1]) || casesAutour.droite > 0 ? colorDisabled : color }}
                        stroke={"white"}
                        strokeWidth={1}
                    />
                </IconButton>
                <div className="fleche-center fleche">
                    <div className="circle-fleche"></div>
                </div>
                <IconButton
                    key={2}
                    className="fleche-bottom fleche"
                    onClick={() => handleClicFleche(2)}
                    disabled={disabled || mursInterdits.includes(mursAutour[2]) || casesAutour.dessous > 0}

                >
                    <ForwardIcon
                        style={{ color: disabled || mursInterdits.includes(mursAutour[2]) || casesAutour.dessous > 0 ? colorDisabled : color }}
                        stroke={"white"}
                        strokeWidth={1}
                    />
                </IconButton>
                <IconButton
                    key={3}
                    className="fleche-gauche fleche"
                    onClick={() => handleClicFleche(3)}
                    disabled={disabled || mursInterdits.includes(mursAutour[3]) || casesAutour.gauche > 0}
                >
                    <ForwardIcon
                        style={{ color: disabled || mursInterdits.includes(mursAutour[3]) || casesAutour.gauche > 0 ? colorDisabled : color }}
                        stroke={"white"}
                        strokeWidth={1}
                    />
                </IconButton>

            </div>


        )
    }

   

    const renderButtons = () => {
        var boutons = []

        if (textes != null) {
            Object.keys(textes).forEach((label, index) => {
                var disabled = true

                if (index < 2 && Object.values(casesAutour).includes(4)) disabled = false // une case en feu autour + extincteur feu
                if (index == 2 && Object.values(casesAutour).includes(3)) disabled = false // une case en fumée autour + extincteur fumée
                if (index == 6 && Object.values(casesAutour).some(el => Math.floor(el / 10) == 2) && (Math.floor(currentCase / 100) == 1 && currentCase % 10 == 0)) disabled = false // une case habitant + transport + pompier non chargé

                if (index == 3 && mursAutour.some(el => el > 0 && (el != 4 && el != 2))) disabled = false // marqueur degats 
                if (index == 4 && mursAutour.includes(5)) disabled = false // porte fermee


                if (index == 5 && mursAutour.includes(6)) disabled = false // porte ouverte

                if (index != 4 && index != 5 || (index == 4 && !mursAutour.includes(6)) || (index == 5 && mursAutour.includes(6))) {

                    boutons.push(
                        <BoutonAction

                            key={label}
                            action={textes[label].contenu}
                            nbIntervention={interventions[label.replace("labelAction", "").replace("Ouvrir", "").replace("Fermer", "")]}
                            nbStock={stocks[label.replace("labelAction", "nb").replace("Ouvrir", "").replace("Fermer", "")]}
                            sourceImage={"assets/" + label.replace("label", "") + ".png"}
                            handleClicAction={handleClicAction}
                            selected={btnClickedBottom == index}
                            num={index}
                            disabled={disabled}
                        />
                    )
                }

            });
        }
        return (
            <div className="boutons-container">
                {boutons}
            </div>
        )
    }

    const renderBoutonsValider = () => {

        return (
            <div className="boutons-valider-container">
                <Button
                    disabled={numeroPompier != tour}
                    key={0}
                    className="bouton-valider vert"
                    variant='contained'
                    disableElevation
                    onClick={() => handleClicValidated(true)}

                >
                    <img src={"assets/validate.png"} alt={"image"} />

                </Button>

                <Button
                    disabled={numeroPompier != tour}
                    key={1}
                    className="bouton-valider rouge"
                    variant='contained'
                    disableElevation
                    onClick={() => handleClicValidated(false)}
                >
                    <img src={"assets/cancel.png"} alt={"image"} />
                </Button>

            </div>
        )

    }
    return (
        <div className="bandeau-bottom">

            {renderFleches()}

            {renderButtons()}

            {renderBoutonsValider()}

        </div>


    );
}

export default BandeauBottom;
