import React,{useState,useEffect} from 'react'
import ToggleButton from '@material-ui/lab/ToggleButton';
import '../../styles/BandeauTop.css';
// import ImageExtFeu from "../../assets/ExtincteurFeu.png"

const BoutonAction = ({
    action,
    nb,
    sourceImage,
    handleClicAction,
    selected,
    num,
    disabled
}) => {
    const [selectedLocal,setSelectedLocal]=useState(false)

    useEffect(()=>{
        console.log(selectedLocal)
    },[selectedLocal])
    return (  
        <ToggleButton  
        disabled={disabled}
        selected={selected && selectedLocal}  
        className="bouton-action" 
        variant='contained' 
        disableElevation 
        onClick={()=>{
            if(![4,5].includes(num)){
                setSelectedLocal(selectedLocal=>!selectedLocal)

            }
            if(!selectedLocal){
                handleClicAction(num)
            }else{
                handleClicAction(null)
            }
            
            

        } }
            
            >
            {/* <div> */}

            

                <label className="title-action">{action+"\n"}</label>
                <img src={sourceImage} alt={"image"}/>
                {nb>1 ? 
                <label className="count-action">{nb+" interventions"}</label>

                :

                <label className="count-action">{nb+" intervention"}</label>
                }
            {/* </div> */}
            
        </ToggleButton>
            
       

    );
}
 
export default BoutonAction;
