import React from 'react';
import { makeStyles } from '@material-ui/core/styles';
import CaseGrille from "./CaseGrille"
import MurGrille from "./MurGrille"
import Grid from '@material-ui/core/Grid';
import './Grille.css';




const Grille = ({
    nbColonnes,
    nbLignes,
    carte,
    position
}) => {

    const renderGrille=()=>{
        var grille=[]
        for (let i=0;i<nbLignes;i++){
             for(let j=0;j<nbColonnes;j++){
                grille.push( 
                <Grid key={i+","+j} item xs={2} >
                    <CaseGrille 
                        murs={[false,true,false,true]}
                        type={carte[i+position[1]][j+position[0]]}
                    />
                </Grid> )
             }
            
        }
        return grille
    }



    return (  

    <Grid container spacing={0}  style={{
        // backgroundColor:"pink"
    }}>
       {renderGrille()}
    </Grid>
        
        
    );
}
 
export default Grille;
