import { withWidth } from '@material-ui/core';
import zIndex from '@material-ui/core/styles/zIndex';
import React,{useState,useEffect} from 'react'

const MurPiece = ({
    num,
    height,
    width
}) => {
    const epaisseur=5
    
    const colors =[
        "purple",
        "blue",
        "white",
        "gray"
    ]

    // const height = document.getElementById('test').style.height;
    // console.log(height)

   

    return ( 
        <div        
            
            style={{
                
                position:"absolute",
                width:num%2 ? width-epaisseur*2 : epaisseur,
                height:num%2 ?  epaisseur : height-epaisseur*2,
                right:num<2 ? epaisseur :  null,
                left:num>1 ? epaisseur :  null,
                top:num<2 ? 0 :  null,
                bottom:num>1 ? 0 :  null,
                backgroundColor:colors[num],
            }}
        >
        </div>

    );
}
 
export default MurPiece;
