import axios from "axios";
const headers = {
  "Content-Type": "application/json"
};
const urlBack = "http://localhost:4000";

export default {
    companies: function(str){
        const response= axios.post(
            urlBack+"/companies",
            {
                str
            },
            { 
               headers: headers
            }
        )
        
        //console.log(response.data)
        return response
          .then(function(data) {
            var userid = JSON.parse(data);
            console.log(userid);
            return userid;
          })
    }
};
