import { withWidth } from '@material-ui/core';
import zIndex from '@material-ui/core/styles/zIndex';
import React,{useState,useEffect} from 'react'

const MurPiece = ({
    num,
    height,
    width,
    type
}) => {
    const [epaisseur,setEpaisseur]=useState(type>0 ? 4 : 1)
    
    
    const colors =[
        "#E4E4E4", // simple
        "black", // large
        "purple"//porte
    ]

    // const height = document.getElementById('test').style.height;
    // console.log(height)

   

    return ( 
        <div        
            
            style={{
                
                position:"absolute",
                width:num%2 ? epaisseur : (epaisseur<4 ? width-epaisseur*2 : width),
                height:num%2 ?  (epaisseur<4 ? height-epaisseur*2 : height) :  epaisseur ,
                right:num<2 ? (num==0 && epaisseur<4 ? epaisseur : 0) :  0,
                left:num>1 ? (num==2  && epaisseur<4 ? epaisseur : 0) :  0,
                top:num!=2 ? ((num==1 || num==3) && epaisseur<4 ? epaisseur : 0) :  null,
                bottom:num>1 ? 0 :  null,
                backgroundColor:colors[type],
            }}
        >
        </div>

    );
}
 
export default MurPiece;
