import React, { useState, useEffect } from 'react'
import '../../styles/BandeauBottomInter.css';
import CaseDonnee from './panels/CaseDonnee'
import Button from '@material-ui/core/Button'
import ContentsAPI from "../../API/ContentsAPI"


const BandeauBottomInter = ({
    numeroPompier,

    resultats,
}) => {
    const budgetSeq = 8000
    const [budgetSeqDep, setBudgetSeqDep] = useState(0)

    const budgetCycle = 4800
    const [budgetCycleDep, setBudgetCycleDep] = useState(0)
    const tour = 0
    const [textes, setTextes] = useState(null)

    useEffect(() => {
        // console.log(textes)
        // const getTextes = async () => {
        //     const textes = ContentsAPI.getTexts("jeuBandeauBottomInter").then(
        //         (res) => { setTextes(res) }
        //     )
        // }
        // if (textes == null) {
        //     getTextes()
        // }
    }, [textes])

    const getText = (name) => {
        if (textes == null) {
            return name
        } else {
            return textes["label" + name.charAt(0).toUpperCase() + name.slice(1)].contenu
        }
    }

    const couts = {
        coutInterDeplacement: 100,
        coutInterPorte: 100,
        coutInterFeu: 200,
        coutInterHab: 100,
        coutInterMarqueur: 100,
        coutInterFum: 100,
        coutInterFeuFum: 100

    }


    const renderCouts = () => {
        var components = []
        Object.keys(couts).forEach(key => {
            components.push(
                <span className="label-cout-inter">
                    {key + " : "}<span className="label-cout-inter-eur">{couts[key] + " €"}</span>
                </span>
            )
        });
        return (
            <div className="couts-container">
                {components}
            </div>
        )
    }

    const renderBudgets = () => {
        return (
            <div className="budgetSeq-container">
                <div
                    className="case-panel  "
                    style={{
                        flex: 1
                    }}
                >
                    <label
                        className="label-title"
                    >
                        {"Séquence en cours"}
                    </label>
                    <CaseDonnee
                        key={1}
                        name={getText("budgetSeq")}
                        value={budgetSeq+" €"}
                        primary={false}
                        flexFactor={0}
                    />
                    <CaseDonnee
                        key={2}
                        name={getText("budgetDep")}
                        value={budgetSeqDep+" €"}
                        primary={false}
                        flexFactor={0}
                    />
                    <CaseDonnee
                        key={3}
                        name={getText("budgetDispo")}
                        value={budgetSeq-budgetSeqDep+" €"}
                        primary={false}
                        flexFactor={0}
                    />
                </div>
                <div
                    className="case-panel donnees-container "
                    style={{
                        flex: 1
                    }}
                >
                    <label
                        className="label-title"
                    >
                        {"Cycle en cours"}
                    </label>
                    <CaseDonnee
                        key={1}
                        name={getText("nbDecedes")}
                        value={resultats.Decedes}
                        primary={false}
                        flexFactor={0}
                    />
                    <CaseDonnee
                        key={2}
                        name={getText("nbSauves")}
                        value={resultats.Sauves}
                        primary={false}
                        flexFactor={0}
                    />
                    <CaseDonnee
                        key={3}
                        name={getText("nbMarqueurs")}
                        value={resultats.Marqueurs}
                        primary={false}
                        flexFactor={0}
                    />
                </div>
            </div>

        )
    }


    const renderBoutonsValider = () => {

        return (
            <div className="boutons-valider-container">
                <Button
                    disabled={numeroPompier != tour}
                    key={0}
                    className="bouton-valider vert"
                    variant='contained'
                    disableElevation
                // onClick={() => handleClicValidated(true)}

                >
                    <img src={"assets/validate.png"} alt={"image"} />

                </Button>

                <Button
                    disabled={numeroPompier != tour}
                    key={1}
                    className="bouton-valider rouge"
                    variant='contained'
                    disableElevation
                // onClick={() => handleClicValidated(false)}
                >
                    <img src={"assets/cancel.png"} alt={"image"} />
                </Button>

            </div>
        )

    }


    const renderResults = () => {
        return (
            <div
                className="case-panel donnees-container"
                style={{
                    flex: 2
                }}
            >
                <label
                    className="label-title"
                >
                    {getText("resultats") + " " + numeroPompier}
                </label>
                <CaseDonnee
                    key={1}
                    name={getText("nbDecedes")}
                    value={resultats.Decedes}
                    primary={false}
                    flexFactor={0}
                />
                <CaseDonnee
                    key={2}
                    name={getText("nbSauves")}
                    value={resultats.Sauves}
                    primary={false}
                    flexFactor={0}
                />
                <CaseDonnee
                    key={3}
                    name={getText("nbMarqueurs")}
                    value={resultats.Marqueurs}
                    primary={false}
                    flexFactor={0}
                />
            </div>
        )
    }
    return (
        <>
            <div className="bandeau-bottom-left">
                {renderResults()}
            </div>
            <div className="bandeau-bottom-right">
                {renderCouts()}
                {renderBudgets()}
                {renderBoutonsValider()}
            </div>
        </>
    );
}

export default BandeauBottomInter;
