import { withWidth } from '@material-ui/core';
import zIndex from '@material-ui/core/styles/zIndex';
import React,{useState,useEffect} from 'react'

const MurPiece = ({
    num,
    height,
    width,
    type
}) => {
    const [epaisseur,setEpaisseur]=useState(type>0 ? 4 : 1)
    
    
    const colors =[
        "#E4E4E4", // simple
        // "red",
        "black", // large
        "purple"//porte
    ]

    // const height = document.getElementById('test').style.height;
    // console.log(height)

   

    return ( 
        <div        
            
            style={{
                
                position:"absolute",
                width:num%2 ? epaisseur : (epaisseur>1 ? width :width-epaisseur*2),
                height:num%2 ? (epaisseur>1 ? height :height-epaisseur*2) :  epaisseur , 
                right:num<2 ? (num==0 ? (epaisseur>1 ? 0 : epaisseur) : 0) :  null,
                left:num>1 ? (num==2 ? (epaisseur>1 ? 0 : epaisseur)  : 0) :  null,
                top:num!=2 ? (num==1 || num==3 ? (epaisseur>1 ? 0 : epaisseur)  : 0) :  null,
                bottom:num==2 ? 0 :  null,
                backgroundColor:colors[type],
                zIndex: epaisseur>1 ? (type>1 ? 2 : 1) : 0
            }}
        >
        </div>

    );
}
 
export default MurPiece;
